INSERT INTO `authors` VALUES (28, 'Bilgehan Utku', 'bilgehan-utku', '<div><span style=\"color: #5d5851; font-family: Muli, sans-serif; font-size: 16px; text-align: justify; background-color: #ffffff;\">Bilgehan Utku, 1983 yılında Ankara&rsquo;da doğdu. Av. Bilgehan Utku başarılı bir eğitim &ouml;ğretimin ardından girdiği Hukuk Fak&uuml;ltesi&rsquo;nden lisans derecesini aldı.&nbsp;</span><span style=\"box-sizing: border-box; font-weight: bolder; color: #5d5851; font-family: Muli, sans-serif; font-size: 16px; text-align: justify; background-color: #ffffff;\"><a style=\"box-sizing: border-box; color: #0d6efd; text-decoration-line: none; transition: 0.2s;\" href=\"http://www.ankarabarosu.org.tr/BaroLevha.aspx?Sicil=30583\" target=\"_blank\" rel=\"noopener\">Av. Bilgehan UTKU Ankara Barosu</a></span><span style=\"color: #5d5851; font-family: Muli, sans-serif; font-size: 16px; text-align: justify; background-color: #ffffff;\">na kayıtlı olarak Ankara&rsquo;da halen kurucusu olduğu MİL HUKUK &amp; DANIŞMANLIK B&Uuml;ROSU&rsquo;nda meslek hayatına devam etmektedir.</span></div>', 'f953c85f-3b63-4e6b-8020-f11a2255a5d1.jpg', 1, 2, '2025-09-04 08:32:22', '2025-09-05 10:02:47');
INSERT INTO `authors` VALUES (30, 'Emre Asan', 'emre-asan', '<div><span style=\"color: #5d5851; font-family: Muli, sans-serif; font-size: 16px; text-align: justify; background-color: #ffffff;\">1982 yılında Tokat&rsquo;ta doğan Av. Emre ASAN, ilk orta ve lise &ouml;ğrenimini başarıyla bitirmesinin ardından dereceyle girdiği Hukuk Fak&uuml;ltesini bitirerek Lisans derecesini almıştır. Av. Emre ASAN; Askeri Ceza Hukuku, Disiplin Hukuk, Ceza Hukuku, İdare Hukuku, Start-up Hukuku ve Bilişim Hukuku alanlarında hukuki danışmanlık hizmeti vermekte aynı zaman faaliyet konularıyla ilgili sivil toplum &ouml;rg&uuml;tlerine &uuml;ye olarak hukuki &ccedil;alışmalarda bulunmaktadır.</span></div>', '13205ce3-bae1-4b6a-82d4-f25ddcf08039.jpg', 1, 1, '2025-09-04 14:39:06', '2025-09-05 10:02:30');
INSERT INTO `authors` VALUES (31, 'Yusuf Çağlayan', 'yusuf-caglayan', '<div>Bilişim Hukuku &uuml;zerine sekt&ouml;rel deneyimiyle katkılar vermektedir.</div>', '8256be24-7cfb-452b-8f44-bfe064f479b1.png', 1, 3, '2025-09-05 08:07:14', '2025-09-05 08:09:30');

-- ----------------------------
-- Table structure for blogs
-- ----------------------------
DROP TABLE IF EXISTS `blogs`;
CREATE TABLE `blogs`  (
  `id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT,
  `order` int(10) UNSIGNED NOT NULL DEFAULT 0,
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `slug` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `prompt` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `image_url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `category_id` bigint(20) UNSIGNED NULL DEFAULT NULL,
  `gallery_id` bigint(20) UNSIGNED NULL DEFAULT NULL,
  `is_featured` tinyint(1) NOT NULL DEFAULT 0,
  `status` tinyint(1) NOT NULL DEFAULT 1,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `user_id` bigint(20) UNSIGNED NULL DEFAULT NULL,
  `author_id` bigint(20) UNSIGNED NULL DEFAULT NULL,
  `meta_description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `meta_keywords` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `blogs_title_unique`(`title`) USING BTREE,
  UNIQUE INDEX `blogs_slug_unique`(`slug`) USING BTREE,
  INDEX `blogs_category_id_foreign`(`category_id`) USING BTREE,
  INDEX `blogs_gallery_id_foreign`(`gallery_id`) USING BTREE,
  INDEX `blogs_user_id_foreign`(`user_id`) USING BTREE,
  INDEX `blogs_author_id_foreign`(`author_id`) USING BTREE,
  CONSTRAINT `blogs_author_id_foreign` FOREIGN KEY (`author_id`) REFERENCES `authors` (`id`) ON DELETE SET NULL ON UPDATE RESTRICT,
  CONSTRAINT `blogs_category_id_foreign` FOREIGN KEY (`category_id`) REFERENCES `categories` (`id`) ON DELETE SET NULL ON UPDATE RESTRICT,
  CONSTRAINT `blogs_gallery_id_foreign` FOREIGN KEY (`gallery_id`) REFERENCES `galleries` (`id`) ON DELETE SET NULL ON UPDATE RESTRICT,
  CONSTRAINT `blogs_user_id_foreign` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE SET NULL ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 52 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of blogs
-- ----------------------------
INSERT INTO `blogs` VALUES (9, 19, 'Çekişmeli Boşanma Davaları: Aşamaları ve Avukatınızın Rolü', 'cekismeli-bosanma-davalari-asamalari-ve-avukatinizin-rolu', '<p><strong>&Ccedil;ekişmeli Boşanma Davaları: Aşamaları ve Avukatınızın Rol&uuml;</strong></p>\r\n<p>Evliliklerin sonlanması, &ccedil;oğu zaman duygusal ve karmaşık bir s&uuml;re&ccedil;tir. Anlaşmalı boşanma m&uuml;mk&uuml;n olmadığında, &ccedil;iftler &ccedil;ekişmeli bir boşanma davası ile karşı karşıya kalırlar. Bu s&uuml;re&ccedil;, T&uuml;rk hukuku &ccedil;er&ccedil;evesinde belirli aşamalardan ge&ccedil;er ve bu aşamaların her birinde avukatın rol&uuml; olduk&ccedil;a &ouml;nemlidir. Bu blog yazısında, &ccedil;ekişmeli boşanma davasının aşamalarını ve avukatınızın bu s&uuml;re&ccedil;te nasıl yardımcı olacağını detaylı olarak ele alacağız.</p>\r\n<p><strong>1. Dava Dilek&ccedil;esi:</strong></p>\r\n<p>Her şey, boşanma davası a&ccedil;an tarafın (davacı) bir dava dilek&ccedil;esi vermesiyle başlar. Bu dilek&ccedil;e, mahkemeye sunulan ve davanın temelini oluşturan &ouml;nemli bir belgedir. Dilek&ccedil;ede;</p>\r\n<ul>\r\n<li>Davacının ve davalının kimlik bilgileri,</li>\r\n<li>Evlilik tarihi ve s&uuml;resi,</li>\r\n<li>Boşanma nedeni (&ouml;rneğin, aldatma, şiddet, anlaşmazlık),</li>\r\n<li>&Ccedil;ocuk var ise velayet, nafaka ve tescil talebi,</li>\r\n<li>Mal paylaşımı talebi (eğer varsa),</li>\r\n<li>İstenen diğer hususlar (&ouml;rneğin, tazminat talepleri) yer almalıdır.</li>\r\n</ul>\r\n<p><strong>Avukatınızın Rol&uuml;:</strong> Bu aşamada avukatınız, dilek&ccedil;enin hukuki a&ccedil;ıdan doğru ve eksiksiz olmasını sağlar. Delilleri d&uuml;zenler, kanıtları toplar ve mahkemeye ikna edici bir şekilde sunulmasını sağlar. Dilek&ccedil;enin hazırlanması ve sunulması s&uuml;recinde size hukuki danışmanlık verir ve haklarınızı korur.</p>\r\n<p><strong>2. &Ouml;n İnceleme:</strong></p>\r\n<p>Mahkeme, dava dilek&ccedil;esini aldıktan sonra &ouml;n inceleme yapar. Bu aşamada dilek&ccedil;enin eksikliklerinin olup olmadığı, dava şartlarının var olup olmadığı incelenir. Eksik belgeler varsa, mahkeme bunların tamamlanması i&ccedil;in s&uuml;re verir.</p>\r\n<p><strong>Avukatınızın Rol&uuml;:</strong> Avukatınız, &ouml;n inceleme aşamasında mahkeme taleplerini takip eder ve eksiklikleri tamamlar. Mahkeme ile iletişimi sağlar ve s&uuml;re&ccedil; hakkında sizi bilgilendirir.</p>\r\n<p><strong>3. Tahkikat:</strong></p>\r\n<p>&Ouml;n inceleme tamamlandıktan sonra tahkikat aşamasına ge&ccedil;ilir. Bu aşamada, tanık dinlemeleri, bilirkişi incelemeleri ve delillerin değerlendirilmesi gibi işlemler yapılır. Mahkeme, davaya ilişkin t&uuml;m ger&ccedil;ekleri ortaya &ccedil;ıkarmaya &ccedil;alışır.</p>\r\n<p><strong>Avukatınızın Rol&uuml;:</strong> Tahkikat aşamasında avukatınız, m&uuml;vekkilinin haklarını savunur, delilleri sunar, tanık ifadelerini y&ouml;nlendirir ve karşı tarafın delillerini &ccedil;&uuml;r&uuml;tmeye &ccedil;alışır. Mahkeme kararının m&uuml;vekkilinin lehine olmasını sağlamak i&ccedil;in aktif rol oynar. Gerekli ise bilirkişi incelemesi talebinde bulunur ve bilirkişi raporunu değerlendirir.</p>\r\n<p><strong>4. Karar:</strong></p>\r\n<p>Tahkikat aşamasının ardından mahkeme, davaya ilişkin kararını verir. Kararda;</p>\r\n<ul>\r\n<li>Boşanma kararı,</li>\r\n<li>Velayet,</li>\r\n<li>Nafaka,</li>\r\n<li>Mal paylaşımı,</li>\r\n<li>Tazminat gibi konularda kararlar bulunur.</li>\r\n</ul>\r\n<p><strong>Avukatınızın Rol&uuml;:</strong> Avukatınız, kararın hukuki a&ccedil;ıdan doğru ve adil olmasını sağlamak i&ccedil;in &ccedil;aba g&ouml;sterir. Karardan memnun kalmazsanız itiraz s&uuml;recini y&ouml;netir ve &uuml;st mahkemelere başvuruda bulunur.</p>\r\n<p><strong>Sonu&ccedil;:</strong></p>\r\n<p>&Ccedil;ekişmeli boşanma davaları, zorlu ve stresli bir s&uuml;re&ccedil;tir. Bu s&uuml;re&ccedil;te deneyimli bir avukatın rehberliği, haklarınızı korumak ve en iyi sonucu elde etmek i&ccedil;in hayati &ouml;nem taşır. Avukatınız, hukuki prosed&uuml;rler hakkında sizi bilgilendirir, stratejik kararlar almanıza yardımcı olur ve davada sizin adınıza aktif rol oynar. Bu nedenle, bir &ccedil;ekişmeli boşanma davası ile karşı karşıya kaldıysanız, deneyimli bir aile hukuku avukatı ile &ccedil;alışmanız şiddetle tavsiye edilir.</p>', 'Türk hukukunda çekişmeli boşanma davasının aşamalarını ve avukatın rolünü anlatan detaylı bir blog yazısı hazırla. Yazı, dava dilekçesi, ön inceleme, tahkikat ve karar aşamalarını içermeli.', '1fd4360b-bf6b-4e61-b314-50959857e9ce.jpg', 1, 2, 1, 1, '2025-09-03 20:11:36', '2025-09-10 13:45:16', 1, 28, NULL, NULL);
INSERT INTO `blogs` VALUES (10, 18, 'Türk Hukukunda Çekişmeli Boşanma Davası: Aşamalar ve Avukatın Rolü', 'turk-hukukunda-cekismeli-bosanma-davasi-asamalar-ve-avukatin-rolu', '<p><strong>&Ccedil;ekişmeli Boşanma Davaları: Zorlu Bir S&uuml;re&ccedil;, Deneyimli Bir Avukata İhtiya&ccedil;</strong></p>\r\n<p>Evliliklerin sonlanması, her iki taraf i&ccedil;in de duygusal ve yasal olarak zorlu bir s&uuml;re&ccedil; olabilir. Anlaşmalı boşanma m&uuml;mk&uuml;n olmadığında, &ccedil;iftler &ccedil;ekişmeli bir boşanma davasıyla karşı karşıya kalırlar. T&uuml;rk hukukunda &ccedil;ekişmeli boşanma davaları, belirli aşamalardan ge&ccedil;er ve bu s&uuml;re&ccedil;te deneyimli bir avukatın rehberliği b&uuml;y&uuml;k &ouml;nem taşır. Bu yazıda, &ccedil;ekişmeli boşanma davasının aşamalarını ve avukatın bu s&uuml;re&ccedil;teki kritik rol&uuml;n&uuml; detaylı olarak ele alacağız.</p>\r\n<p><strong>1. Dava Dilek&ccedil;esi: Davanın Başlangıcı</strong></p>\r\n<p>&Ccedil;ekişmeli boşanma davası, davacı tarafın (boşanma talebinde bulunan eş) dava dilek&ccedil;esini ilgili aile mahkemesine sunmasıyla başlar. Bu dilek&ccedil;e, davanın &ouml;z&uuml;n&uuml; oluşturur ve aşağıdaki bilgileri i&ccedil;ermelidir:</p>\r\n<ul>\r\n<li><strong>Tarafların kimlik bilgileri:</strong> Ad, soyad, T.C. kimlik numarası, adres bilgileri vb.</li>\r\n<li><strong>Evlilik bilgileri:</strong> Evlilik tarihi, yeri ve nikah memurunun adı.</li>\r\n<li><strong>Boşanma nedeni:</strong> T&uuml;rk Medeni Kanunu\'nda belirtilen boşanma sebeplerinden birinin (&ouml;rneğin, eşlerden birinin kusuru, yaşam birlikteliğinin temelinden sarsılması) detaylı ve kanıtlarıyla birlikte a&ccedil;ıklanması.</li>\r\n<li><strong>Talepler:</strong> Boşanma talebinin yanı sıra, velayet, nafaka, tazminat, mal paylaşımı gibi konularda taleplerin a&ccedil;ık ve net bir şekilde belirtilmesi. &Ouml;rneğin, &ccedil;ocukların velayetinin kime verileceği, nafaka miktarı ve &ouml;deme şekli, manevi tazminat talebi ve miktarı, mal paylaşımı hususunda talepler vb.</li>\r\n<li><strong>Kanıtlar:</strong> Dilek&ccedil;eye, boşanma talebini destekleyen t&uuml;m kanıtlar (fotoğraflar, mesajlar, tanık ifadeleri, bilirkişi raporları vb.) eklenmelidir.</li>\r\n</ul>\r\n<p><strong>Avukatın Rol&uuml;:</strong> Avukat, dava dilek&ccedil;esinin doğru ve eksiksiz olarak hazırlanmasını sağlar, t&uuml;m gerekli kanıtların toplanmasına yardımcı olur ve mahkemeye sunulmasını y&ouml;netir. Ayrıca, davacının haklarını ve y&uuml;k&uuml;ml&uuml;l&uuml;klerini a&ccedil;ıklar ve dava stratejisini belirler.</p>\r\n<p><strong>2. &Ouml;n İnceleme:</strong></p>\r\n<p>Mahkeme, dava dilek&ccedil;esini inceledikten sonra &ouml;n inceleme aşamasına ge&ccedil;er. Bu aşamada, davanın usul&uuml;ne uygun olup olmadığı, eksik belgelerin olup olmadığı kontrol edilir. Eksik belgelerin tamamlanması veya usul eksikliklerinin giderilmesi i&ccedil;in taraflara s&uuml;re verilir.</p>\r\n<p><strong>Avukatın Rol&uuml;:</strong> Avukat, mahkemenin talep ettiği ek belgeleri zamanında ve doğru şekilde temin etmeyi sağlar ve usul eksikliklerinin giderilmesinde aktif rol oynar.</p>\r\n<p><strong>3. Tahkikat:</strong></p>\r\n<p>&Ouml;n inceleme aşaması tamamlandıktan sonra tahkikat aşamasına ge&ccedil;ilir. Bu aşamada, mahkeme delilleri inceler ve tarafları dinler. Tanık ifadeleri alınır, bilirkişi raporları değerlendirilir ve tarafların sunduğu diğer kanıtlar incelenir.</p>\r\n<p><strong>Avukatın Rol&uuml;:</strong> Avukat, m&uuml;vekkilinin haklarını savunur, delilleri sunar, karşı tarafın delillerine karşı savunma yapar ve mahkemeyi ikna edici bir şekilde y&ouml;nlendirir. Tanıkların ifadelerini y&ouml;nlendirir, gerekli soruları sorar ve karşı tarafın tanıklarını &ccedil;&uuml;r&uuml;tmeye &ccedil;alışır.</p>\r\n<p><strong>4. Karar Aşaması:</strong></p>\r\n<p>Tahkikat aşamasının ardından mahkeme, toplanan delillere ve kanıtlara dayanarak bir karar verir. Bu karar, boşanmanın kabul edilip edilmediğini, velayet, nafaka, tazminat ve mal paylaşımı gibi konularda nasıl bir karar verildiğini belirtir. Karar, kesinleşene kadar temyiz edilebilir.</p>\r\n<p><strong>Avukatın Rol&uuml;:</strong> Avukat, mahkemenin kararını inceler, gerekirse temyiz başvurusunda bulunur ve m&uuml;vekkilinin haklarının korunmasını sağlar.</p>\r\n<p><strong>Sonu&ccedil;:</strong></p>\r\n<p>&Ccedil;ekişmeli boşanma davaları, karmaşık ve duygusal a&ccedil;ıdan zorlu s&uuml;re&ccedil;lerdir. Deneyimli bir avukatın rehberliği, bu s&uuml;re&ccedil;te m&uuml;vekkilin haklarının korunması ve en iyi sonucun elde edilmesi i&ccedil;in son derece &ouml;nemlidir. Avukat, sadece yasal prosed&uuml;rler konusunda bilgi sahibi olmakla kalmaz, aynı zamanda m&uuml;vekkilini duygusal olarak da destekler ve doğru stratejinin belirlenmesinde yardımcı olur. Bu nedenle, &ccedil;ekişmeli bir boşanma davasıyla karşı karşıya kalındığında, deneyimli bir aile hukuku avukatıyla &ccedil;alışmak b&uuml;y&uuml;k &ouml;nem taşır.</p>', NULL, '181b1637-5968-4411-b410-55d5cff6aa7e.jpg', 1, 2, 1, 1, '2025-09-03 20:39:12', '2025-09-10 13:45:16', NULL, 28, NULL, NULL);
INSERT INTO `blogs` VALUES (18, 16, 'Türk Hukukunda Çekişmeli Boşanma Davası: Aşamalar ve Avukatın Önemli Rolü', 'turk-hukukunda-cekismeli-bosanma-davasi-asamalar-ve-avukatin-onemli-rolu', '<p><strong>&Ccedil;ekişmeli Boşanma Davası: Zorlu Bir S&uuml;re&ccedil;, Deneyimli Bir Avukatla Daha Y&ouml;netilebilir</strong></p>\r\n<p>Evliliklerin son bulması, taraflar i&ccedil;in her zaman kolay ve acısız bir s&uuml;re&ccedil; değildir. &Ouml;zellikle &ccedil;ekişmeli boşanma davaları, duygusal zorlukların yanı sıra karmaşık yasal s&uuml;re&ccedil;leri de beraberinde getirir. Bu yazıda, T&uuml;rk hukukunda &ccedil;ekişmeli boşanma davasının aşamalarını ve bu s&uuml;re&ccedil;te avukatın kritik rol&uuml;n&uuml; detaylı olarak ele alacağız.</p>\r\n<p><strong>1. Dava Dilek&ccedil;esinin Hazırlanması ve Sunumu:</strong></p>\r\n<p>&Ccedil;ekişmeli boşanma davası, davacı tarafın (boşanmayı isteyen eş) mahkemeye dava dilek&ccedil;esini sunmasıyla başlar. Bu dilek&ccedil;e, olduk&ccedil;a &ouml;nemlidir ve davayı şekillendiren temel belgedir. Dilek&ccedil;ede;</p>\r\n<ul>\r\n<li><strong>Tarafların kimlik bilgileri:</strong> Ad, soyad, adres, T.C. kimlik numarası gibi bilgiler yer almalıdır.</li>\r\n<li><strong>Evlilik bilgileri:</strong> Nikah tarihi, yeri ve evlilik c&uuml;zdanı numarası gibi bilgiler belirtilmelidir.</li>\r\n<li><strong>Boşanma nedeni:</strong> Boşanma talebinin gerek&ccedil;esi detaylı ve kanıtlanabilir bir şekilde a&ccedil;ıklanmalıdır. (&ouml;rneğin; aldatma, şiddet, hayata kast, terk etme vb.) Bu aşamada, her t&uuml;rl&uuml; delil (fotoğraf, tanık ifadeleri, mesaj kayıtları vb.) dilek&ccedil;eye eklenmelidir.</li>\r\n<li><strong>Talepler:</strong> Boşanma dışında, nafaka, velayet, mal paylaşımı gibi konularda talepler net bir şekilde belirtilmelidir. &Ouml;rneğin, &ccedil;ocukların velayetinin kime verileceği, ne kadar nafaka &ouml;deneceği, taşınmaz malların nasıl paylaşılacağı gibi konulara dair ayrıntılı talepler ileri s&uuml;r&uuml;lmelidir.</li>\r\n</ul>\r\n<p><strong>Avukatın Rol&uuml;:</strong> Dava dilek&ccedil;esinin hukuki olarak doğru ve eksiksiz bir şekilde hazırlanması, mahkeme nezdinde g&uuml;&ccedil;l&uuml; bir ilk izlenim yaratmak i&ccedil;in hayati &ouml;nem taşır. Deneyimli bir avukat, davacı adına doğru ve etkili bir dilek&ccedil;e hazırlayarak, davaya sağlam bir temel oluşturur.</p>\r\n<p><strong>2. &Ouml;n İnceleme:</strong></p>\r\n<p>Mahkeme, dava dilek&ccedil;esini aldıktan sonra, dosyanın eksikliklerini tespit etmek ve davaya ilişkin ilk değerlendirmeyi yapmak i&ccedil;in &ouml;n inceleme yapar. Bu aşamada, eksik evrakların tamamlanması istenebilir veya tarafların uzlaşma g&ouml;r&uuml;şmelerine y&ouml;nlendirilmesi sağlanabilir.</p>\r\n<p><strong>Avukatın Rol&uuml;:</strong> Avukat, bu aşamada mahkemenin taleplerini takip eder, eksiklikleri tamamlar ve uzlaşma g&ouml;r&uuml;şmelerinde m&uuml;vekkilini temsil ederek en iyi sonucu elde etmesine yardımcı olur.</p>\r\n<p><strong>3. Tahkikat (Duruşma):</strong></p>\r\n<p>&Ouml;n inceleme aşamasından sonra, dava tahkikat aşamasına ge&ccedil;er. Bu aşamada, taraflar, tanıkları ve sunacakları delillerle mahkemeye &ccedil;ıkar. Mahkeme, tarafların beyanlarını dinler, delilleri inceler ve bilirkişi raporlarına başvurabilir.</p>\r\n<p><strong>Avukatın Rol&uuml;:</strong> Avukat, m&uuml;vekkilinin haklarını koruyarak, g&uuml;&ccedil;l&uuml; bir savunma oluşturur. Tanıkların ifadelerini y&ouml;nlendirir, delilleri sunar ve mahkemeyi ikna edici bir şekilde savunur. Karşı tarafın iddialarına karşı etkili bir şekilde cevap verir ve olası itirazları değerlendirir.</p>\r\n<p><strong>4. Karar Aşaması:</strong></p>\r\n<p>Tahkikat aşamasının ardından, mahkeme dosyayı inceleyerek kararını verir. Karar, tarafların t&uuml;m taleplerini kapsayacak şekilde ayrıntılı bir şekilde yazılır ve gerek&ccedil;elendirilir. Karar, her iki taraf i&ccedil;in de bağlayıcıdır.</p>\r\n<p><strong>Avukatın Rol&uuml;:</strong> Avukat, mahkemenin kararını değerlendirir, gerekirse itiraz yoluna başvurur ve m&uuml;vekkilinin haklarını korumak i&ccedil;in gerekli t&uuml;m yasal işlemleri yapar. Hukuki s&uuml;re&ccedil; i&ccedil;erisinde, olası riskler ve se&ccedil;enekleri m&uuml;vekkiline detaylı bir şekilde a&ccedil;ıklar.</p>\r\n<p><strong>Sonu&ccedil;:</strong></p>\r\n<p>&Ccedil;ekişmeli boşanma davaları, hem duygusal hem de yasal a&ccedil;ıdan zorlu s&uuml;re&ccedil;lerdir. Bu s&uuml;re&ccedil;te deneyimli ve uzman bir avukata sahip olmak, m&uuml;vekkilin haklarını koruması, en iyi sonucu elde etmesi ve olası riskleri minimize etmesi a&ccedil;ısından hayati &ouml;nem taşır. Avukat, sadece yasal işlemleri y&uuml;r&uuml;tmekle kalmaz, aynı zamanda m&uuml;vekkiline hukuki danışmanlık vererek, s&uuml;re&ccedil; boyunca destek ve rehberlik sağlar. Unutmayın ki, doğru avukat se&ccedil;imi, başarıya giden yolda olduk&ccedil;a &ouml;nemli bir adımdır.</p>', NULL, '53d7fdc8-811c-4af5-9b29-7f03b98234c9.jpg', 2, 3, 1, 1, '2025-09-04 14:23:16', '2025-09-10 13:45:16', NULL, 28, NULL, NULL);
INSERT INTO `blogs` VALUES (19, 17, 'Türk Hukukunda Çekişmeli Boşanma Davasının Aşamaları ve Avukatın Rolü', 'turk-hukukunda-cekismeli-bosanma-davasinin-asamalari-ve-avukatin-rolu', '<p><strong>&Ccedil;ekişmeli Boşanma Davası: Zorlu Bir S&uuml;recin Adımları ve Avukatın &Ouml;nemi</strong></p>\r\n<p>Evliliklerin sona ermesi, her zaman kolay ve huzurlu bir s&uuml;re&ccedil; değildir. Anlaşmalı boşanma m&uuml;mk&uuml;n olmadığında, &ccedil;iftler &ccedil;ekişmeli boşanma davası ile karşı karşıya kalırlar. Bu s&uuml;re&ccedil;, hukuki prosed&uuml;rlerin karmaşıklığı ve duygusal y&uuml;k&uuml; nedeniyle olduk&ccedil;a zorlayıcı olabilir. Bu yazıda, T&uuml;rk hukukunda &ccedil;ekişmeli boşanma davasının aşamalarını ve bu s&uuml;re&ccedil;te avukatın hayati rol&uuml;n&uuml; detaylı olarak ele alacağız.</p>\r\n<p><strong>1. Dava Dilek&ccedil;esi:</strong></p>\r\n<p>&Ccedil;ekişmeli boşanma davası, davalı eşin ikametgahının bulunduğu yerdeki aile mahkemesinde a&ccedil;ılır. Davacı eş (davayı a&ccedil;an taraf) tarafından hazırlanan dava dilek&ccedil;esi, davanın temelini oluşturur. Dilek&ccedil;ede;</p>\r\n<ul>\r\n<li><strong>Tarafların kimlik bilgileri:</strong> Ad, soyad, adres, T.C. kimlik numarası gibi bilgiler yer alır.</li>\r\n<li><strong>Evlilik bilgileri:</strong> Evlilik tarihi, yeri ve nikah memurunun adı gibi bilgiler belirtilir.</li>\r\n<li><strong>Boşanma sebepleri:</strong> Boşanma talebinin dayanağı olan sebepler ayrıntılı ve delillerle desteklenerek a&ccedil;ıklanır (&ouml;rneğin; eşin ge&ccedil;imini sağlamaması, hayata kast, şiddet, ağır kusurlu davranışlar). Kanıtlar (tanık beyanları, fotoğraflar, mesaj kayıtları, sağlık raporları vb.) dilek&ccedil;eye eklenir.</li>\r\n<li><strong>Talebin konusu:</strong> Boşanma talebinin yanı sıra, nafaka, velayet, mal paylaşımı gibi konulardaki talepler de dilek&ccedil;ede belirtilir.</li>\r\n<li><strong>İstenen deliller:</strong> Davayı destekleyen delillerin mahkemeye sunulması istenir.</li>\r\n</ul>\r\n<p>Avukat, bu dilek&ccedil;enin hukuki a&ccedil;ıdan doğru ve eksiksiz hazırlanmasında son derece &ouml;nemli bir rol oynar. Yanlış veya eksik bir dilek&ccedil;e, davanın reddi veya istenen sonu&ccedil;ların elde edilememesiyle sonu&ccedil;lanabilir.</p>\r\n<p><strong>2. &Ouml;n İnceleme:</strong></p>\r\n<p>Mahkeme, dava dilek&ccedil;esini ve eklerini inceleyerek davanın usul&uuml;ne uygun olup olmadığını ve a&ccedil;ılma şartlarını taşıyıp taşımadığını kontrol eder. Bu aşamada eksiklikler varsa, mahkeme davacıya eksiklikleri tamamlaması i&ccedil;in s&uuml;re verir. Avukat, bu aşamada mahkeme tarafından istenen t&uuml;m eksiklikleri zamanında ve doğru bir şekilde tamamlamayı sağlar.</p>\r\n<p><strong>3. Tahkikat (İspat Aşaması):</strong></p>\r\n<p>&Ouml;n inceleme sonrasında dava tahkikat aşamasına ge&ccedil;er. Bu aşamada;</p>\r\n<ul>\r\n<li><strong>Tanık dinlenmesi:</strong> Davacı ve davalı eşin sunduğu tanıklar mahkeme tarafından dinlenir.</li>\r\n<li><strong>Delil incelemesi:</strong> Sunulan deliller (fotoğraf, video, mesaj kayıtları, raporlar vb.) mahkeme tarafından incelenir.</li>\r\n<li><strong>Bilirkişi incelemesi:</strong> Gerektiğinde, bilirkişi raporu alınır (&ouml;rneğin; psikolojik rapor).</li>\r\n<li><strong>Taraf savunmaları:</strong> Davacı ve davalı eşler, kendi g&ouml;r&uuml;şlerini ve savunmalarını sunarlar.</li>\r\n</ul>\r\n<p>Avukat, bu aşamada m&uuml;vekkilinin haklarını korumak i&ccedil;in &ouml;nemli bir rol oynar. Tanıkların hazırlanması, delillerin toplanması ve sunulması, mahkeme karşısındaki savunmanın etkili bir şekilde yapılması gibi konularda avukatın uzmanlığı kritik &ouml;neme sahiptir.</p>\r\n<p><strong>4. Karar Aşaması:</strong></p>\r\n<p>Tahkikat aşamasının ardından mahkeme, toplanan delilleri ve tarafların savunmalarını değerlendirerek kararını verir. Kararda;</p>\r\n<ul>\r\n<li><strong>Boşanma kararı:</strong> Boşanma talebinin kabul edilip edilmediği belirtilir.</li>\r\n<li><strong>Nafaka:</strong> Eşlerden birine nafaka &ouml;denmesine karar verilebilir.</li>\r\n<li><strong>Velayet:</strong> &Ccedil;ocukların velayetinin kime verileceği belirlenir.</li>\r\n<li><strong>Mal paylaşımı:</strong> Ortak malların nasıl paylaşılacağı belirlenir.</li>\r\n</ul>\r\n<p>Avukat, mahkemenin kararına karşı itiraz edilebilir ise, itiraz s&uuml;recinin y&ouml;netilmesinde ve daha &uuml;st mahkemelerde davanın takip edilmesinde m&uuml;vekkiline destek olur.</p>\r\n<p><strong>Avukatın Rol&uuml;n&uuml;n &Ouml;nemi:</strong></p>\r\n<p>&Ccedil;ekişmeli boşanma davaları, hukuki ve duygusal a&ccedil;ıdan olduk&ccedil;a karmaşıktır. Bir avukatın rehberliği, bu s&uuml;re&ccedil;te olduk&ccedil;a &ouml;nemlidir. Avukat;</p>\r\n<ul>\r\n<li>Hukuki s&uuml;re&ccedil;leri bilmekte ve davayı etkili bir şekilde y&ouml;netmektedir.</li>\r\n<li>M&uuml;vekkilinin haklarını korumaktadır.</li>\r\n<li>Delilleri toplamada ve sunmada yardımcı olmaktadır.</li>\r\n<li>M&uuml;vekkilini mahkeme karşısında temsil etmektedir.</li>\r\n<li>Anlaşmaya varma yolunda arabuluculuk yapabilir.</li>\r\n</ul>\r\n<p>Sonu&ccedil; olarak, &ccedil;ekişmeli bir boşanma davası, olduk&ccedil;a zorlu bir s&uuml;re&ccedil;tir. Uzman bir avukatın desteği, bu s&uuml;re&ccedil;te hem hukuki haklarınızı korumak hem de duygusal y&uuml;k&uuml; hafifletmek i&ccedil;in olmazsa olmazdır. Bu nedenle, &ccedil;ekişmeli bir boşanma davası ile karşı karşıya kalındığında, deneyimli bir aile hukuku avukatı ile &ccedil;alışmak b&uuml;y&uuml;k &ouml;nem taşımaktadır.</p>', NULL, '0e5f0dc8-1075-4260-9e66-f2454831d0a7.jpg', 1, 2, 1, 1, '2025-09-04 14:38:46', '2025-09-10 13:45:16', NULL, 28, NULL, NULL);
INSERT INTO `blogs` VALUES (20, 15, 'İşçi-İşveren Uyuşmazlıklarında Hukuki Çözüm Yolları: Arabuluculuktan İcra Takibine**', 'isci-isveren-uyusmazliklarinda-hukuki-cozum-yollari-arabuluculuktan-icra-takibine', '<p>**</p>\r\n<p>İş hayatında, iş&ccedil;i ve işveren arasında &ccedil;eşitli uyuşmazlıklar ortaya &ccedil;ıkabilir. Bu uyuşmazlıklar, &uuml;cret alacakları, kıdem tazminatı, haksız işten &ccedil;ıkarmalar, &ccedil;alışma koşulları gibi bir&ccedil;ok konuyu kapsayabilir. T&uuml;rk Hukuku, bu uyuşmazlıkların &ccedil;&ouml;z&uuml;m&uuml; i&ccedil;in farklı yollar sunmaktadır. Bu yolların en etkili ve hızlı olanını se&ccedil;mek, hem iş&ccedil;i hem de işveren i&ccedil;in b&uuml;y&uuml;k &ouml;nem taşımaktadır. Bu yazıda, iş&ccedil;i-işveren uyuşmazlıklarının &ccedil;&ouml;z&uuml;m&uuml;nde başvurulabilecek yolları adım adım inceleyeceğiz.</p>\r\n<p><strong>1. Arabuluculuk: &Ccedil;&ouml;z&uuml;m&uuml;n Dostane Yolu</strong></p>\r\n<p>Uyuşmazlıkların &ccedil;&ouml;z&uuml;m&uuml;nde ilk tercih edilebilecek y&ouml;ntem arabuluculuktur. Arabuluculuk, tarafsız bir arabulucu eşliğinde, tarafların kendi aralarında anlaşmaya varmalarını sağlayan bir alternatif uyuşmazlık &ccedil;&ouml;z&uuml;m yoludur. Hızlı, esnek ve maliyet etkin bir y&ouml;ntem olan arabuluculuk, mahkeme s&uuml;recinin getirdiği gecikmelerden ve masraflardan ka&ccedil;ınmayı sağlar. Arabuluculuk s&uuml;reci gizlidir ve tarafların anlaşması halinde ortaya &ccedil;ıkan uzlaşma, mahkeme kararı h&uuml;km&uuml;nde kesinleşir. İş&ccedil;i ve işveren, arabuluculuk s&uuml;recine başlamadan &ouml;nce bir arabulucu se&ccedil;ebilirler veya yetkili kurumlar aracılığıyla arabulucu atanmasını isteyebilirler.</p>\r\n<p><strong>2. Dava A&ccedil;ma Şartları: Mahkeme Yolculuğu Başlıyor</strong></p>\r\n<p>Arabuluculuk s&uuml;reci sonu&ccedil;suz kalırsa veya arabuluculuğa başvurulmazsa, iş&ccedil;i veya işveren iş mahkemesinde dava a&ccedil;abilir. Dava a&ccedil;abilmek i&ccedil;in bazı şartların yerine getirilmesi gerekir. &Ouml;ncelikle, uyuşmazlığın iş hukuku kapsamında olması, dava a&ccedil;ma s&uuml;resinin ge&ccedil;memiş olması ve dava dilek&ccedil;esinde gerekli bilgilerin ve belgelerin eksiksiz bulunması gerekmektedir. Dava dilek&ccedil;esi, yetkili iş mahkemesine sunulur. Yetkili mahkeme, iş s&ouml;zleşmesinin imzalandığı veya işin yapıldığı yerdeki iş mahkemesidir.</p>\r\n<p><strong>3. İş Mahkemesi Yargılaması: Hakkı Savunma S&uuml;reci</strong></p>\r\n<p>Dava a&ccedil;ıldıktan sonra, iş mahkemesi yargılaması başlar. Bu s&uuml;re&ccedil;te, taraflar delillerini sunar, tanık dinletilir ve bilirkişi incelemesi yapılabilir. Mahkeme, toplanan delilleri ve yasal d&uuml;zenlemeleri değerlendirerek karar verir. Yargılama s&uuml;reci, davanın karmaşıklığına bağlı olarak değişmekle birlikte, zaman alıcı olabilir.</p>\r\n<p><strong>4. Kararın İcrası: Hak Edilenin Ger&ccedil;ekleşmesi</strong></p>\r\n<p>İş mahkemesinin verdiği karar kesinleştikten sonra, kararın icrası aşamasına ge&ccedil;ilir. Eğer karar alacağın &ouml;denmesi y&ouml;n&uuml;ndeyse, alacaklı taraf, icra dairesine başvurarak bor&ccedil;lunun malvarlığına haciz uygulanmasını isteyebilir. İcra takibi, mahkeme kararının uygulanmasını sağlar. İcra takibi s&uuml;recinde, bor&ccedil;luya &ccedil;eşitli ihtarlar g&ouml;nderilir ve &ouml;deme yapması i&ccedil;in s&uuml;re tanınır. &Ouml;deme yapılmaması durumunda, bor&ccedil;lunun mallarına haciz uygulanır ve satışa &ccedil;ıkarılır.</p>\r\n<p><strong>Sonu&ccedil;:</strong></p>\r\n<p>İş&ccedil;i-işveren uyuşmazlıklarında, arabuluculuk, hızlı ve maliyet etkin bir &ccedil;&ouml;z&uuml;m yolu sunmaktadır. Ancak, arabuluculuk başarısız olursa, iş mahkemesinde dava a&ccedil;ılarak hak arama yoluna gidilebilir. İş mahkemesi yargılaması, uzun ve karmaşık bir s&uuml;re&ccedil; olabildiğinden, deneyimli bir avukattan destek almak b&uuml;y&uuml;k &ouml;nem taşır. Kesinleşen mahkeme kararlarının icrası ise, icra daireleri tarafından ger&ccedil;ekleştirilir. Bu s&uuml;re&ccedil;lerin her birinde, doğru adımların atılması ve hukuki s&uuml;re&ccedil;lerin bilinmesi, hakların korunması i&ccedil;in olduk&ccedil;a &ouml;nemlidir.</p>', NULL, 'd6b93589-e4d3-4352-8235-64d674ec282e.jpg', 1, NULL, 0, 1, '2025-09-04 19:13:09', '2025-09-10 13:45:16', NULL, 28, NULL, NULL);
INSERT INTO `blogs` VALUES (21, 14, 'Ceza Yargılamasında Savunma Hakkınız: Bilmeniz Gereken Her Şey', 'ceza-yargilamasinda-savunma-hakkiniz-bilmeniz-gereken-her-sey', '<p>Adalet sisteminin temel taşlarından biri ş&uuml;phesiz ki savunma hakkıdır. Su&ccedil;lamalarla karşı karşıya kalan herkesin, kendisini savunmak, su&ccedil;suzluğunu kanıtlamak veya cezasını hafifletmek i&ccedil;in gerekli olan her t&uuml;rl&uuml; yasal ara&ccedil;tan yararlanma hakkına sahip olması demokratik hukuk d&uuml;zeninin olmazsa olmazıdır. Bu blog yazısında, ceza yargılamasında savunma hakkının kapsamını, ş&uuml;pheli ve sanığın haklarını, m&uuml;dafinin rol&uuml;n&uuml; ve savunmanın nasıl kullanılacağını detaylı olarak ele alacağız.</p>\r\n<p><strong>Ş&uuml;pheli ve Sanığın Hakları:</strong></p>\r\n<p>Ceza yargılamasının başlangıcından itibaren, ş&uuml;pheli ve daha sonra sanık konumuna gelen kişi, belirli haklara sahiptir. Bu haklar, adil bir yargılama i&ccedil;in olmazsa olmazdır ve Anayasa ile g&uuml;vence altındadır. En &ouml;nemlilerinden bazıları şunlardır:</p>\r\n<ul>\r\n<li><strong>Susma hakkı:</strong> Ş&uuml;pheli, kendisine y&ouml;neltilen su&ccedil;lamalarla ilgili olarak ifade vermek zorunda değildir. Verdiği ifadenin aleyhine kullanılabileceğini bilmesi ve bu hakkını kullanmaktan &ccedil;ekinmemesi gerekir.</li>\r\n<li><strong>Avukat tutma hakkı:</strong> Herkes, kendisini savunması i&ccedil;in bir avukat tutma hakkına sahiptir. Maddi durumu yetersiz olanlar i&ccedil;in ise, kamu tarafından atanmış bir avukattan yararlanma imkanı mevcuttur.</li>\r\n<li><strong>Delillere erişim hakkı:</strong> Sanık ve avukatı, kendisine isnat edilen su&ccedil;lamayla ilgili delillere erişebilir ve bunları inceleyebilir. Bu, savunmanın hazırlanması ve etkili bir şekilde kullanılması i&ccedil;in son derece &ouml;nemlidir.</li>\r\n<li><strong>Tanık dinletme hakkı:</strong> Sanık, kendisini savunmak i&ccedil;in tanık dinletebilir ve deliller sunabilir.</li>\r\n<li><strong>Yargılanma hakkı:</strong> Herkes, adil ve tarafsız bir mahkemede yargılanma hakkına sahiptir.</li>\r\n</ul>\r\n<p><strong>M&uuml;dafinin Rol&uuml;:</strong></p>\r\n<p>M&uuml;dafi, sanığın haklarını korumada hayati bir rol oynar. M&uuml;dafinin g&ouml;revleri şunlardır:</p>\r\n<ul>\r\n<li><strong>Sanığın haklarını savunmak:</strong> M&uuml;dafi, sanığın t&uuml;m yasal haklarının korunmasını sağlar ve bu hakların ihlali durumunda gerekli yasal işlemleri başlatır.</li>\r\n<li><strong>Savunma stratejisi geliştirmek:</strong> M&uuml;dafi, dosyayı dikkatlice inceleyerek en uygun savunma stratejisini belirler ve bunu uygular.</li>\r\n<li><strong>Delilleri değerlendirmek:</strong> M&uuml;dafi, toplanan delilleri dikkatlice değerlendirir, savunmanın aleyhine olan delilleri &ccedil;&uuml;r&uuml;tmeye &ccedil;alışır ve lehte olan delilleri sunar.</li>\r\n<li><strong>Tanıklarla g&ouml;r&uuml;şmek ve ifadelerini hazırlamak:</strong> M&uuml;dafi, sanığın lehine tanıklarla g&ouml;r&uuml;ş&uuml;r ve ifadelerini hazırlar.</li>\r\n<li><strong>Mahkemede sanığı temsil etmek:</strong> M&uuml;dafi, mahkeme s&uuml;recinde sanığı temsil eder ve savunmasını sunar.</li>\r\n</ul>\r\n<p><strong>Sorgu ve Duruşma Aşamalarında Savunma Hakkının Kullanımı:</strong></p>\r\n<p>Savunma hakkı, sorgu ve duruşma aşamalarında etkin bir şekilde kullanılmalıdır.</p>\r\n<ul>\r\n<li><strong>Sorgu aşamasında:</strong> Ş&uuml;pheli, susma hakkını kullanabilir veya avukatıyla g&ouml;r&uuml;şerek ifade verebilir. Avukat, sorgulama sırasında ş&uuml;phelinin haklarının korunmasını sağlar.</li>\r\n<li><strong>Duruşma aşamasında:</strong> Sanık ve avukatı, delilleri sunar, tanıkları dinletir, savunma g&ouml;r&uuml;şlerini a&ccedil;ıklar ve yargıca taleplerini iletir. Savunma, su&ccedil;lamaları reddetmeyi, su&ccedil;un unsurlarının oluşmadığını iddia etmeyi, cezanın hafifletilmesini talep etmeyi veya başka uygun savunma stratejilerini kullanmayı i&ccedil;erebilir.</li>\r\n</ul>\r\n<p><strong>Sonu&ccedil;:</strong></p>\r\n<p>Savunma hakkı, adil bir yargılamanın temel bir unsurudur. Ş&uuml;pheli ve sanıkların bu haklarından tam olarak yararlanabilmeleri i&ccedil;in, iyi bir avukatla &ccedil;alışmaları ve yasal haklarını bilmeleri son derece &ouml;nemlidir. Bu blog yazısı, savunma hakkının kapsamı hakkında genel bir bakış sunmaktadır. Herhangi bir hukuki sorunla karşı karşıya kaldığınızda, bir avukattan hukuki danışmanlık almanız &ouml;nemlidir.</p>', NULL, '23bd375c-a367-4de6-a04a-1ac67feb988d.jpg', 1, NULL, 0, 1, '2025-09-04 19:13:46', '2025-09-10 13:45:16', NULL, 28, NULL, NULL);
INSERT INTO `blogs` VALUES (22, 13, 'Miras Paylaşımı: Adım Adım Bir Rehber', 'miras-paylasimi-adim-adim-bir-rehber', '<p>Miras paylaşımı, sevdiklerimizi kaybetmenin &uuml;z&uuml;nt&uuml;s&uuml;ne eklenen karmaşık bir s&uuml;re&ccedil; olabilir. Ancak, doğru adımları izleyerek ve gerekli yasal prosed&uuml;rleri bilerek bu s&uuml;reci daha y&ouml;netilebilir hale getirebilirsiniz. Bu blog yazısında, miras paylaşımının temel aşamalarını adım adım a&ccedil;ıklayacağız.</p>\r\n<p><strong>1. Terk Tespitinin Yapılması:</strong></p>\r\n<p>Her şeyden &ouml;nce, &ouml;len kişinin (m&uuml;r&uuml;n) bıraktığı malların (terke) tam ve doğru bir şekilde tespit edilmesi gerekir. Bu, banka hesaplarından gayrimenkullere, değerli eşyalardan bor&ccedil;lara kadar her şeyi kapsar. Terke dahil olan t&uuml;m varlıklar detaylı bir liste halinde d&uuml;zenlenmelidir. Bu liste, miras paylaşımı s&uuml;recinde olduk&ccedil;a &ouml;nemlidir. Eğer miras bırakan bir vasiyetname bıraktıysa, bu belge bu aşamada incelenir ve mirasın paylaşımı vasiyetnameye uygun olarak yapılır. Vasiyetname yoksa miras paylaşımı T&uuml;rk Medeni Kanunu\'ndaki kurallara g&ouml;re d&uuml;zenlenir.</p>\r\n<p><strong>2. Miras&ccedil;ılık Belgesinin Alınması:</strong></p>\r\n<p>Terke ait varlıklar tespit edildikten sonra, miras&ccedil;ıların kim olduğunun belirlenmesi ve miras&ccedil;ılık belgesinin alınması gerekir. Bu belge, resmi makamlarca verilen ve miras&ccedil;ıların kimliklerini ve miras paylarını g&ouml;steren bir belgedir. Miras&ccedil;ılık belgesi almak i&ccedil;in, noterlikten miras&ccedil;ıların kimliklerini belirten bir belge talep edilir. Bu işlem i&ccedil;in gerekli evraklar noter tarafından talep edilecektir ve genellikle n&uuml;fus kayıt &ouml;rneği, &ouml;l&uuml;m belgesi ve miras bırakanın kimlik bilgileri gibi belgeler bulunur.</p>\r\n<p><strong>3. Miras Paylaşımında Anlaşma:</strong></p>\r\n<p>Miras&ccedil;ılar arasında karşılıklı anlayış ve iyi niyet varsa, mirasın paylaşımı karşılıklı anlaşma yoluyla ger&ccedil;ekleştirilebilir. Bu, en hızlı ve en masrafsız y&ouml;ntemdir. Anlaşma, miras&ccedil;ıların t&uuml;m&uuml; tarafından imzalanan bir miras paylaşım s&ouml;zleşmesi ile resmiyete d&ouml;k&uuml;l&uuml;r. Bu s&ouml;zleşme noter tarafından d&uuml;zenlenmelidir. Anlaşmanın i&ccedil;eriği, her bir miras&ccedil;ının hangi varlığı alacağını, varlıkların değerlerini ve varsa alacakların nasıl &ouml;deneceğini detaylı olarak belirtir.</p>\r\n<p><strong>4. Miras Paylaşımında Dava S&uuml;reci:</strong></p>\r\n<p>Eğer miras&ccedil;ılar arasında anlaşma sağlanamazsa, miras paylaşımı i&ccedil;in mahkemeye başvurmak gerekir. Miras davası a&ccedil;ıldığında, mahkeme, tarafların sunacağı deliller ve kanıtlara g&ouml;re mirasın adil bir şekilde paylaştırılmasına karar verir. Bu s&uuml;re&ccedil;, anlaşmaya g&ouml;re daha uzun ve maliyetli olabilir. Davada, avukat tutmak gerekebilir ve mahkeme masrafları da &ouml;denmelidir.</p>\r\n<p><strong>5. Mirasın Taksimi:</strong></p>\r\n<p>Mirasın paylaşımı, anlaşma veya mahkeme kararı ile sonu&ccedil;landıktan sonra, mirasın taksimi işlemi ger&ccedil;ekleştirilir. Bu işlemde, her bir miras&ccedil;ıya d&uuml;şen pay teslim edilir. Gayrimenkuller, banka hesapları ve diğer varlıkların taksimi, noter huzurunda yapılır.</p>\r\n<p><strong>Sonu&ccedil;:</strong></p>\r\n<p>Miras paylaşımı, hassas ve karmaşık bir s&uuml;re&ccedil;tir. Bu s&uuml;re&ccedil;te, doğru bilgiye sahip olmak ve yasal prosed&uuml;rlere uygun hareket etmek, hem zaman hem de maliyet a&ccedil;ısından b&uuml;y&uuml;k avantaj sağlar. Anlaşmazlıkları &ouml;nlemek ve s&uuml;reci kolaylaştırmak i&ccedil;in, miras&ccedil;ıların m&uuml;mk&uuml;n olduğunca karşılıklı anlayış ve işbirliği i&ccedil;erisinde hareket etmeleri &ouml;nemlidir. Ancak, anlaşmazlık durumunda, deneyimli bir avukatın danışmanlığı b&uuml;y&uuml;k &ouml;nem taşır.</p>', NULL, '56425f80-a979-4534-8384-6a4f0da47dfb.jpg', 1, NULL, 0, 1, '2025-09-04 19:14:18', '2025-09-10 13:45:16', NULL, 30, NULL, NULL);
INSERT INTO `blogs` VALUES (23, 12, 'İcra ve İflas Hukukunda Borçlunun Hak ve Yükümlülükleri: Bir Rehber] **', 'icra-ve-iflas-hukukunda-borclunun-hak-ve-yukumlulukleri-bir-rehber', '<p>**</p>\r\n<p>İcra ve iflas hukuku, bor&ccedil;lular i&ccedil;in karmaşık ve zorlayıcı bir s&uuml;re&ccedil; olabilir. Bu s&uuml;re&ccedil;te haklarınızı bilmek ve y&uuml;k&uuml;ml&uuml;l&uuml;klerinizi yerine getirmek, olumsuz sonu&ccedil;lardan ka&ccedil;ınmak i&ccedil;in hayati &ouml;nem taşır. Bu yazı, icra ve iflas s&uuml;recindeki bor&ccedil;lunun hak ve y&uuml;k&uuml;ml&uuml;l&uuml;klerini &ouml;zetleyerek size rehberlik etmeyi ama&ccedil;lamaktadır.</p>\r\n<p><strong>&Ouml;deme Emri Aşaması:</strong></p>\r\n<p>Alacaklı, borcunuzu tahsil etmek i&ccedil;in icra takibi başlatabilir. Bu s&uuml;re&ccedil; genellikle bir &ouml;deme emriyle başlar. &Ouml;deme emrinde, borcun tutarı, &ouml;deme s&uuml;resi ve &ouml;deme yapılmazsa ne olacağı belirtilir. Bu aşamada, <strong>bor&ccedil;lunun en &ouml;nemli hakkı, &ouml;deme emrine karşı itiraz etme hakkıdır.</strong> Eğer itirazınızı haklı nedenlerle belirli bir s&uuml;re i&ccedil;inde yapmazsanız, &ouml;deme emri kesinleşir ve icra işlemleri başlar. <strong>Y&uuml;k&uuml;ml&uuml;l&uuml;ğ&uuml;n&uuml;z ise, emri zamanında alıp inceleyerek, haklı bir itirazınız varsa, belirtilen s&uuml;re i&ccedil;inde itirazınızı ibraz etmektir.</strong> Haklı bir itirazınız yoksa, borcu &ouml;demeyi d&uuml;ş&uuml;nmelisiniz.</p>\r\n<p><strong>Mal Beyanı Aşaması:</strong></p>\r\n<p>&Ouml;deme emrine itiraz edilmesi veya &ouml;deme yapılmaması durumunda, icra m&uuml;d&uuml;r&uuml; mal beyanı isteyebilir. <strong>Bor&ccedil;lunun y&uuml;k&uuml;ml&uuml;l&uuml;ğ&uuml;, sahip olduğu t&uuml;m malları eksiksiz ve doğru bir şekilde beyan etmektir.</strong> Yanlış veya eksik beyan, cezai yaptırımlara yol a&ccedil;abilir. <strong>Bor&ccedil;lunun hakkı ise, doğru ve tam bir beyan verdikten sonra, haklı gerek&ccedil;elerle hacize karşı itirazda bulunabilmesidir.</strong> &Ouml;rneğin, haciz edilemeyecek malların (mesken, zorunlu ihtiya&ccedil; eşyaları vb.) haciz edilmesi durumunda itiraz hakkı mevcuttur.</p>\r\n<p><strong>Haciz Aşaması:</strong></p>\r\n<p>Mal beyanı sonrasında, alacaklı adına haciz işlemleri başlatılabilir. Haciz, bor&ccedil;lunun mallarının satılması ve borcun tahsili amacıyla el konulması anlamına gelir. <strong>Bu aşamada bor&ccedil;lunun hakkı, hacize itiraz edebilmek ve mahkemeye başvurarak haciz kararının iptalini talep edebilmektir.</strong> <strong>Y&uuml;k&uuml;ml&uuml;l&uuml;ğ&uuml; ise, haciz memuruna gereken kolaylığı sağlamaktır.</strong> Haciz işlemlerini engellemek veya zorlaştırmak, cezai sonu&ccedil;lar doğurabilir.</p>\r\n<p><strong>İflas Aşaması:</strong></p>\r\n<p>Bor&ccedil;lu, &ouml;deme g&uuml;c&uuml;n&uuml; kaybettiğinde ve alacaklılarının taleplerini karşılayamadığında, iflas yoluna başvurabilir veya alacaklılar tarafından iflas davası a&ccedil;ılabilir. <strong>İflas, hem bor&ccedil;lu hem de alacaklılar i&ccedil;in hukuki bir s&uuml;re&ccedil;tir.</strong> <strong>Bor&ccedil;lunun hakkı, iflas masasına t&uuml;m mal varlığını bildirerek, iflas s&uuml;recinde kendisine tanınan hakları kullanmaktır.</strong> <strong>Y&uuml;k&uuml;ml&uuml;l&uuml;ğ&uuml; ise, iflas masasına mal varlığını doğru ve eksiksiz beyan etmek, mahkeme tarafından istenen bilgi ve belgeleri sunmaktır.</strong> İflas s&uuml;recinde bor&ccedil;lunun bazı hakları kısıtlanırken, aynı zamanda bor&ccedil;larından kurtulma imkanı da bulabilir.</p>\r\n<p><strong>&Ouml;zetle:</strong></p>\r\n<p>İcra ve iflas s&uuml;re&ccedil;leri, bor&ccedil;lu i&ccedil;in olduk&ccedil;a karmaşık olabilir. Bu s&uuml;re&ccedil;te haklarınızı ve y&uuml;k&uuml;ml&uuml;l&uuml;klerinizi bilmek, doğru adımları atmak ve olumsuz sonu&ccedil;lardan korunmak i&ccedil;in son derece &ouml;nemlidir. Bu yazıda yer alan bilgiler genel nitelikte olup, hukuki danışmanlık niteliğinde değildir. İcra ve iflas s&uuml;recinde mutlaka bir avukattan hukuki destek almanız tavsiye edilir.</p>', NULL, 'aa8a7ad2-f4a4-468d-bf79-851eddd45025.jpg', 1, NULL, 0, 1, '2025-09-04 19:15:01', '2025-09-10 13:45:16', NULL, 30, NULL, NULL);
INSERT INTO `blogs` VALUES (24, 11, 'Türk Medeni Kanunu\'nda Velayet Davası: Aşamaları ve Avukatın Önemli Rolü', 'turk-medeni-kanununda-velayet-davasi-asamalari-ve-avukatin-onemli-rolu', '<p>Velayet davaları, aile hukukunun en hassas ve karmaşık konularından birini oluşturur. &Ccedil;ocukların geleceği ve refahı s&ouml;z konusu olduğunda, doğru yasal s&uuml;re&ccedil;lerin takip edilmesi ve deneyimli bir avukatın rehberliği son derece &ouml;nemlidir. Bu blog yazısında, T&uuml;rk Medeni Kanunu&rsquo;na g&ouml;re velayet davalarının aşamalarını ve avukatın bu s&uuml;re&ccedil;teki rol&uuml;n&uuml; detaylı olarak ele alacağız.</p>\r\n<p><strong>Dava A&ccedil;ma Şartları:</strong></p>\r\n<p>Velayet davası a&ccedil;abilmek i&ccedil;in &ouml;ncelikle bir sebep g&ouml;stermek gerekmektedir. T&uuml;rk Medeni Kanunu, &ccedil;ocuğun menfaatlerini g&ouml;zeterek velayetin değiştirilmesini veya paylaştırılmasını d&uuml;zenler. Bu sebepler, ebeveynlerden birinin &ccedil;ocuğun bakımına ve eğitimine gereken &ouml;zeni g&ouml;stermemesi, k&ouml;t&uuml; alışkanlıkları olması, &ccedil;ocuğun sağlığını ve gelişimini olumsuz etkileyecek davranışlarda bulunması, evlilik birliğinin temelinden sarsılması gibi durumları i&ccedil;erebilir. Davanın a&ccedil;ılabilmesi i&ccedil;in &ccedil;ocuğun velayetinin hen&uuml;z belirlenmemiş olması veya mevcut velayetin değiştirilmesi gerektiği iddiasının kanıtlanması şarttır.</p>\r\n<p><strong>Dava Aşamaları:</strong></p>\r\n<p><strong>1. &Ouml;n İnceleme:</strong></p>\r\n<p>Dava dilek&ccedil;esiyle birlikte mahkemeye sunulan belgeler ve deliller &ouml;n incelemeye tabi tutulur. Mahkeme, dilek&ccedil;enin eksikliklerini tamamlar ve dava a&ccedil;ma şartlarının sağlanıp sağlanmadığını değerlendirir. Bu aşamada avukat, eksik belgeleri tamamlamak, iddiaları net ve anlaşılır bir şekilde ortaya koymak ve mahkemenin talebini karşılayacak şekilde dilek&ccedil;eyi hazırlamakla sorumludur.</p>\r\n<p><strong>2. Tahkikat:</strong></p>\r\n<p>&Ouml;n inceleme sonrasında dava kabul edilirse tahkikat aşamasına ge&ccedil;ilir. Bu aşamada, mahkeme, davaya konu olan olay ve durumları aydınlatmak i&ccedil;in &ccedil;eşitli delilleri inceleyecektir. Bunlar arasında tanık ifadeleri, bilirkişi raporları, fotoğraflar, mesajlar ve diğer belgeler yer alabilir. Avukat, m&uuml;vekkilinin haklarını korumak ve mahkemeye sunacağı delilleri etkili bir şekilde sunmak i&ccedil;in bu aşamada aktif rol oynar. Tanık dinlemelerine hazırlanır, gerekli belgeleri toplar ve mahkemeye sunar.</p>\r\n<p><strong>3. Uzman Raporu:</strong></p>\r\n<p>Velayet davalarında &ccedil;ocuğun menfaatleri &ouml;n plandadır. Bu nedenle, &ccedil;ocuğun psikolojik ve sosyal durumunu değerlendiren uzman raporları b&uuml;y&uuml;k &ouml;nem taşır. Mahkeme, &ccedil;ocuk psikoloğu veya sosyal hizmet uzmanı gibi uzmanlardan rapor isteyebilir. Avukat, uzman raporunun objektif ve &ccedil;ocuğun menfaatlerini yansıtan bir şekilde hazırlanmasını sağlamak i&ccedil;in &ccedil;aba g&ouml;sterir. Raporun i&ccedil;eriğini inceleyerek, gerekirse ek incelemeler talep edebilir veya itirazlarını dile getirebilir.</p>\r\n<p><strong>4. Karar S&uuml;reci:</strong></p>\r\n<p>T&uuml;m deliller ve uzman raporları değerlendirildikten sonra mahkeme kararını verir. Karar, &ccedil;ocuğun &uuml;st&uuml;n yararını g&ouml;zeterek verilir ve velayetin kimde olacağı, velayetin paylaştırılıp paylaştırılmayacağı, nafaka miktarı gibi konuları kapsar. Avukat, m&uuml;vekkilinin haklarını savunmak ve mahkemenin vereceği kararı etkilemek i&ccedil;in bu aşamada da &ouml;nemli bir rol oynar. Karara karşı itiraz yolu mevcuttur.</p>\r\n<p><strong>Avukatın Rol&uuml;:</strong></p>\r\n<p>Avukat, velayet davasında m&uuml;vekkilinin haklarını korumak, hukuki s&uuml;re&ccedil;leri y&ouml;netmek ve en iyi sonucu elde etmek i&ccedil;in hayati bir rol oynar. Avukatın g&ouml;revleri arasında;</p>\r\n<ul>\r\n<li>Dava dilek&ccedil;esinin hazırlanması ve sunulması,</li>\r\n<li>Gerekli delillerin toplanması ve sunulması,</li>\r\n<li>Mahkeme &ouml;n&uuml;nde m&uuml;vekkili temsil etme,</li>\r\n<li>Uzman raporlarının incelenmesi ve değerlendirilmesi,</li>\r\n<li>Karara karşı itiraz edilmesi,</li>\r\n<li>M&uuml;vekkile hukuki danışmanlık verilmesi yer almaktadır.</li>\r\n</ul>\r\n<p>Velayet davaları olduk&ccedil;a duygusal ve stresli olabilir. Deneyimli bir avukat, m&uuml;vekkilini bu s&uuml;re&ccedil;te desteklemek, hukuki haklarını korumak ve en iyi sonucu elde etmesine yardımcı olmak i&ccedil;in olmazsa olmazdır. Doğru avukat se&ccedil;imi, &ccedil;ocuğun geleceği i&ccedil;in kritik &ouml;neme sahiptir.</p>', NULL, 'bac6247e-ecae-45e9-9129-e730e13544e6.jpg', 3, NULL, 0, 1, '2025-09-04 19:15:56', '2025-09-10 13:45:16', NULL, 30, NULL, NULL);
INSERT INTO `blogs` VALUES (25, 10, 'Tutuklama Tedbiri: Şartları, İtiraz Yolları ve Alternatifleri', 'tutuklama-tedbiri-sartlari-itiraz-yollari-ve-alternatifleri', '<p><strong>Tutuklama Tedbiri: &Ouml;zg&uuml;rl&uuml;ğ&uuml;n Kısıtlanması ve Hukuki Koruma</strong></p>\r\n<p>Ceza hukukunda tutuklama, ş&uuml;phelinin veya sanığın yargılama s&uuml;reci boyunca &ouml;zg&uuml;rl&uuml;ğ&uuml;nden yoksun bırakılması anlamına gelen olduk&ccedil;a ağır bir tedbirdir. Bu tedbirin uygulanması, temel hak ve &ouml;zg&uuml;rl&uuml;klerin sınırlandırılması anlamına geldiğinden, ancak kesinlikle gerekli olduğu durumlarda ve hukukun &ouml;ng&ouml;rd&uuml;ğ&uuml; şartlar &ccedil;er&ccedil;evesinde uygulanabilir. Bu yazıda, tutuklamanın şartlarını, itiraz yollarını ve alternatif koruma tedbirlerini detaylı olarak inceleyeceğiz.</p>\r\n<p><strong>Tutuklama Kararı İ&ccedil;in Gerekli Koşullar:</strong></p>\r\n<p>Bir kimsenin tutuklanabilmesi i&ccedil;in, ilgili mevzuatta a&ccedil;ık&ccedil;a belirtilen bazı koşulların bir arada bulunması gerekir. Bu koşullar, genellikle şu şekilde &ouml;zetlenebilir:</p>\r\n<ul>\r\n<li><strong>Su&ccedil;un Ciddiyeti:</strong> Tutuklama, genellikle ağır cezalı su&ccedil;larda veya kamu g&uuml;venliği a&ccedil;ısından b&uuml;y&uuml;k tehlike oluşturan su&ccedil;larda uygulanır. Su&ccedil;un niteliği, ceza miktarı ve su&ccedil;un işleniş bi&ccedil;imi, tutuklama kararının verilip verilmemesinde &ouml;nemli rol oynar.</li>\r\n<li><strong>Ka&ccedil;ma Ş&uuml;phesi:</strong> Ş&uuml;pheli veya sanığın yargılama s&uuml;recinden ka&ccedil;ma ihtimalinin y&uuml;ksek olması, tutuklama kararı i&ccedil;in &ouml;nemli bir gerek&ccedil;edir. Bu durum, şahsın yurt dışı bağlantıları, mal varlığının durumu, daha &ouml;nceki ka&ccedil;ma girişimleri gibi unsurlar g&ouml;z &ouml;n&uuml;nde bulundurularak değerlendirilir.</li>\r\n<li><strong>Delilleri Karartma veya Tanıklara Tesir Etme Tehlikesi:</strong> Ş&uuml;pheli veya sanığın, yargılama s&uuml;recini etkileyecek şekilde delilleri karartma veya tanıklar &uuml;zerinde baskı kurma ihtimalinin bulunması da tutuklama i&ccedil;in yeterli bir gerek&ccedil;e olarak kabul edilir.</li>\r\n<li><strong>Su&ccedil;un Tekrar İşlenmesi Tehlikesi:</strong> Ş&uuml;pheli veya sanığın, aynı veya benzer su&ccedil;ları tekrar işleme olasılığının y&uuml;ksek olması durumunda, kamu g&uuml;venliğini korumak amacıyla tutuklama kararı verilebilir.</li>\r\n</ul>\r\n<p><strong>Tutuklama S&uuml;resi:</strong></p>\r\n<p>Tutuklama, belirli bir s&uuml;reyle sınırlıdır. Tutuklama s&uuml;resi, su&ccedil;un niteliğine ve yargılamanın aşamasına bağlı olarak değişir. Uzun s&uuml;reli tutuklamalar, mahkeme tarafından periyodik olarak g&ouml;zden ge&ccedil;irilir ve gerek&ccedil;esinin devam edip etmediği kontrol edilir. S&uuml;rekli olarak tutukluluğun devamı i&ccedil;in yeterli sebep g&ouml;sterilemezse, kişi serbest bırakılır.</p>\r\n<p><strong>Tutuklama Kararına İtiraz Yolları:</strong></p>\r\n<p>Tutuklama kararı, ş&uuml;pheli veya sanığın ve avukatının itiraz hakkına tabidir. İtirazlar, genellikle ilgili mahkemeye veya &uuml;st mahkemelere yapılır. İtiraz dilek&ccedil;esinde, tutuklama kararının gerek&ccedil;elerinin yetersizliği, alternatif koruma tedbirlerinin uygulanabilirliği gibi hususlar ileri s&uuml;r&uuml;l&uuml;r. İtiraz s&uuml;reci, tutuklamanın yasallığı ve adil yargılama hakkının korunması a&ccedil;ısından b&uuml;y&uuml;k &ouml;nem taşır.</p>\r\n<p><strong>Alternatif Koruma Tedbirleri:</strong></p>\r\n<p>Tutuklama, &ouml;zg&uuml;rl&uuml;ğ&uuml;n en ağır kısıtlanması olduğu i&ccedil;in, m&uuml;mk&uuml;n olduğunca alternatif koruma tedbirleri tercih edilmelidir. Bu tedbirler arasında:</p>\r\n<ul>\r\n<li><strong>Adli Kontrol:</strong> Ş&uuml;pheli veya sanığın belirli şartlara bağlı kalarak &ouml;zg&uuml;rl&uuml;ğ&uuml;ne devam etmesine izin veren bir tedbirdir. Bu şartlar, yurt dışına &ccedil;ıkış yasağı, belirli yerlere gitme yasağı, belirli kişilerle g&ouml;r&uuml;şme yasağı gibi hususları i&ccedil;erebilir.</li>\r\n<li><strong>Keşide:</strong> Ş&uuml;pheli veya sanığın, belirli bir s&uuml;re i&ccedil;in belirli bir yere gitmesini ve orada kalmasını zorunlu kılan bir tedbirdir.</li>\r\n<li><strong>Ev hapsi:</strong> Ş&uuml;pheli veya sanığın kendi evinde veya belirlenen bir yerde kalmasını ve belirli şartlara uymasını zorunlu kılan bir tedbirdir.</li>\r\n</ul>\r\n<p><strong>Sonu&ccedil;:</strong></p>\r\n<p>Tutuklama tedbiri, son derece &ouml;nemli ve &ouml;zg&uuml;rl&uuml;ğ&uuml; ciddi şekilde kısıtlayan bir hukuki işlemdir. Uygulaması, sadece hukukun kesinlikle &ouml;ng&ouml;rd&uuml;ğ&uuml; şartlar altında ve yargı denetimi altında ger&ccedil;ekleşmelidir. Alternatif koruma tedbirlerinin etkin bir şekilde kullanımı, gereksiz tutuklamaları &ouml;nleyerek adil yargılamanın teminat altına alınmasına katkıda bulunur. Herhangi bir tutuklama kararında, hukuki haklarınızı bilmek ve uzman bir avukattan destek almak son derece &ouml;nemlidir.</p>', NULL, 'f649f365-5504-451b-aa28-b7e9888ddf95.jpg', 3, NULL, 0, 1, '2025-09-04 19:17:09', '2025-09-10 13:45:16', NULL, 30, NULL, NULL);
INSERT INTO `blogs` VALUES (26, 5, 'Türk Borçlar Kanunu\'nda Kira Sözleşmelerinden Doğan Uyuşmazlıkların Çözümü: Tahliye, Tespit ve Arabuluculuk', 'turk-borclar-kanununda-kira-sozlesmelerinden-dogan-uyusmazliklarin-cozumu-tahliye-tespit-ve-arabuluculuk', '<p>Kira s&ouml;zleşmeleri, g&uuml;nl&uuml;k hayatta sık&ccedil;a karşılaşılan ve bir&ccedil;ok hukuki uyuşmazlığa yol a&ccedil;abilen s&ouml;zleşmelerdir. T&uuml;rk Bor&ccedil;lar Kanunu (TBK), bu uyuşmazlıkların &ccedil;&ouml;z&uuml;m&uuml; i&ccedil;in &ccedil;eşitli yollar sunmaktadır. Kiracı ve kiraya veren arasında ortaya &ccedil;ıkan anlaşmazlıklar, genellikle tahliye davaları, kira tespit davaları ve giderek daha fazla tercih edilen arabuluculuk yoluyla &ccedil;&ouml;z&uuml;lmeye &ccedil;alışılır.</p>\r\n<p><strong>1. Tahliye Davaları:</strong></p>\r\n<p>Kira s&ouml;zleşmesinin sona ermesinden sonra kiracının taşınmazı boşaltmaması durumunda kiraya veren, TBK\'nın ilgili maddeleri uyarınca tahliye davası a&ccedil;abilir. Tahliye davasının a&ccedil;ılabilmesi i&ccedil;in ge&ccedil;erli bir fesih sebebinin bulunması şarttır. Bu sebepler TBK\'da detaylı olarak belirtilmiştir ve bunlar arasında;</p>\r\n<ul>\r\n<li><strong>S&ouml;zleşme s&uuml;resinin dolması:</strong> Kira s&ouml;zleşmesinin belirli s&uuml;reli olması ve s&uuml;renin dolması en yaygın tahliye sebebidir.</li>\r\n<li><strong>Kira bedelinin &ouml;denmemesi:</strong> Kiracı, kira bedelini veya diğer &ouml;deme y&uuml;k&uuml;ml&uuml;l&uuml;klerini yerine getirmediğinde kiraya veren tahliye davası a&ccedil;abilir.</li>\r\n<li><strong>S&ouml;zleşmenin ihlali:</strong> Kiracı, s&ouml;zleşme şartlarını ihlal etmesi durumunda (&ouml;rneğin, taşınmazı ama&ccedil; dışında kullanması, tadilat yapması vb.) kiraya veren tahliye davası a&ccedil;abilir.</li>\r\n<li><strong>Kiracının k&ouml;t&uuml; niyeti:</strong> Kiracının k&ouml;t&uuml; niyetli davranışları (&ouml;rneğin, taşınmazı tahrip etmesi, komşularla sorun &ccedil;ıkarması) da tahliye sebebi olabilir.</li>\r\n</ul>\r\n<p>Tahliye davasında mahkeme, delilleri inceleyerek ve tarafların savunmalarını dinleyerek karar verir. Karar, gerek&ccedil;eli olarak yazılır ve taraflarca temyiz edilebilir.</p>\r\n<p><strong>2. Kira Tespit Davaları:</strong></p>\r\n<p>Kira s&ouml;zleşmesinde kira bedeli veya diğer &ouml;deme koşulları konusunda anlaşmazlık olması durumunda, taraflar kira tespit davası a&ccedil;abilirler. Bu davada mahkeme, s&ouml;zleşmenin şartlarını, tarafların beyanlarını ve piyasa koşullarını g&ouml;z &ouml;n&uuml;nde bulundurarak adil bir kira bedeli tespit eder. Kira tespit davasında, tarafların sundukları deliller, bilirkişi raporları ve benzeri unsurlar mahkemenin kararını etkileyebilir.</p>\r\n<p><strong>3. Arabuluculuk:</strong></p>\r\n<p>Arabuluculuk, tarafların anlaşmazlıklarını bağımsız ve tarafsız bir arabulucu aracılığıyla &ccedil;&ouml;zmelerine imkan tanıyan alternatif bir uyuşmazlık &ccedil;&ouml;z&uuml;m y&ouml;ntemidir. TBK\'nın &ouml;ng&ouml;rd&uuml;ğ&uuml; arabuluculuk s&uuml;recinde, taraflar kendi aralarında uzlaşmaya &ccedil;alışırlar ve arabulucu bu s&uuml;re&ccedil;te kolaylaştırıcı bir rol oynar. Arabuluculuk, dava s&uuml;re&ccedil;lerine g&ouml;re daha hızlı, daha az maliyetli ve daha uzlaşmacı bir &ccedil;&ouml;z&uuml;m sunar. Anlaşma sağlanması durumunda, arabuluculuk anlaşması mahkeme kararı h&uuml;km&uuml;nde uygulanabilir. Ancak arabuluculuk başarısızlıkla sonu&ccedil;lanırsa, taraflar mahkemeye başvurma hakkına sahiptirler.</p>\r\n<p>Sonu&ccedil; olarak, T&uuml;rk Bor&ccedil;lar Kanunu kira s&ouml;zleşmelerinden doğan uyuşmazlıkların &ccedil;&ouml;z&uuml;m&uuml; i&ccedil;in &ccedil;eşitli yollar sunmaktadır. Taraflar, anlaşmazlıklarının &ccedil;&ouml;z&uuml;m&uuml; i&ccedil;in &ouml;ncelikle arabuluculuk yolunu deneyebilirler. Ancak, arabuluculuk başarısız olursa ya da doğrudan mahkemeye başvurmayı tercih ederlerse, tahliye ve kira tespit davaları yoluyla haklarını arayabilirler. Hangi y&ouml;ntemin se&ccedil;ileceği, uyuşmazlığın niteliğine, tarafların tercihlerine ve diğer ilgili fakt&ouml;rlere bağlıdır. Bu nedenle, hukuki danışmanlık almak, doğru ve etkili bir &ccedil;&ouml;z&uuml;m yolunu belirlemek a&ccedil;ısından &ouml;nemlidir.</p>', NULL, '679e05a3-de61-426c-b4b3-56bce11a2707.jpg', 3, NULL, 0, 1, '2025-09-04 19:18:13', '2025-09-10 13:45:29', NULL, 30, NULL, NULL);
INSERT INTO `blogs` VALUES (27, 9, 'İdare Hukukunda Tam Yargı Davalarının Aşamaları: Bir Rehber', 'idare-hukukunda-tam-yargi-davalarinin-asamalari-bir-rehber', '<p>İdare hukuku, kamu y&ouml;netiminin faaliyetlerinin yasalara uygunluğunu denetleyen ve vatandaşların haklarını koruyan geniş bir alandır. İdare hukuku davaları i&ccedil;erisinde, idarenin eylemlerine karşı a&ccedil;ılan tam yargı davaları, idarenin eylemlerinin hukuka uygunluğunun kapsamlı bir şekilde incelenmesini sağlar. Bu yazıda, idare hukukunda tam yargı davalarının aşamalarını, dava a&ccedil;ma şartlarından itiraz yollarına kadar detaylı bir şekilde ele alacağız.</p>\r\n<p><strong>1. Dava A&ccedil;ma Şartları:</strong></p>\r\n<p>Bir tam yargı davası a&ccedil;abilmek i&ccedil;in bazı şartların yerine getirilmesi gerekir. Bunlar;</p>\r\n<ul>\r\n<li><strong>Dava a&ccedil;ma yetkisi:</strong> Davacı, idarenin eylemine karşı dava a&ccedil;ma yetkisine sahip olmalıdır. Bu yetki, genellikle idarenin eyleminin doğrudan davacıya y&ouml;nelik olması ve davacının bu eylemden zarar g&ouml;rmesiyle sağlanır.</li>\r\n<li><strong>Yasal s&uuml;re:</strong> Dava, idarenin eyleminin &ouml;ğrenildiği tarihten itibaren belirli bir s&uuml;re i&ccedil;erisinde a&ccedil;ılmalıdır. Bu s&uuml;re, ilgili mevzuata g&ouml;re değişir. S&uuml;renin ge&ccedil;mesi halinde dava zamanaşımına uğrar.</li>\r\n<li><strong>Dava konusu:</strong> Dava konusu, idarenin hukuka aykırı bir eylemi olmalıdır. Yani, idarenin eyleminin yasalara, t&uuml;z&uuml;klere veya genel idari ilkelerine aykırı olması gerekmektedir.</li>\r\n<li><strong>Dilek&ccedil;e şartları:</strong> Dava dilek&ccedil;esi, ilgili mevzuatta belirtilen şartlara uygun olarak hazırlanmalıdır. Dilek&ccedil;ede davacının kimliği, dava konusu, idarenin eyleminin &ouml;zeti, istenen hukuki sonu&ccedil; ve deliller yer almalıdır.</li>\r\n</ul>\r\n<p><strong>2. Dilek&ccedil;enin İncelenmesi:</strong></p>\r\n<p>Dava dilek&ccedil;esi mahkemeye ulaştıktan sonra, mahkeme tarafından usul y&ouml;n&uuml;nden incelenir. Dilek&ccedil;ede eksiklikler varsa, mahkeme davacıya eksiklikleri tamamlaması i&ccedil;in s&uuml;re verir. Eksiklikler tamamlanmazsa, dava reddedilir. Dilek&ccedil;e usul y&ouml;n&uuml;nden eksiksiz ise, dava kabul edilir ve esas hakkında incelemeye başlanır.</p>\r\n<p><strong>3. Bilirkişi ve Keşif:</strong></p>\r\n<p>Davanın niteliğine g&ouml;re, mahkeme bilirkişi incelemesi veya keşif yapılması kararını verebilir. Bilirkişi, uzman g&ouml;r&uuml;ş&uuml; sunmak i&ccedil;in g&ouml;revlendirilirken, keşif, olay yerinin incelenmesi veya delillerin yerinde g&ouml;r&uuml;lmesi amacıyla ger&ccedil;ekleştirilir. Bu aşamalar, mahkemenin olay ve delilleri daha iyi anlamasına yardımcı olur.</p>\r\n<p><strong>4. Delillerin Değerlendirilmesi:</strong></p>\r\n<p>Mahkeme, dava dosyasındaki t&uuml;m delilleri (dilek&ccedil;eler, belgeler, tanık ifadeleri, bilirkişi raporları vb.) değerlendirir. Delillerin ağırlığı, inandırıcılığı ve ge&ccedil;erliliği dikkate alınarak, idarenin eyleminin hukuka uygunluğu veya aykırılığı tespit edilir.</p>\r\n<p><strong>5. Karar:</strong></p>\r\n<p>Delillerin değerlendirilmesinin ardından mahkeme kararını verir. Karar, idarenin eyleminin hukuka uygun olduğu veya aykırı olduğu y&ouml;n&uuml;nde olabilir. Hukuka aykırı bulunan bir eylemin iptali, idarenin yeni bir işlem yapması veya davacıya tazminat &ouml;denmesi gibi sonu&ccedil;lar doğurabilir.</p>\r\n<p><strong>6. İtiraz Yolları:</strong></p>\r\n<p>Mahkemenin verdiği karar, ilgili taraflar tarafından itiraz edilebilir. İtiraz yolları, kararın niteliğine ve mahkemenin mertebesine g&ouml;re değişir. &Ouml;rneğin, bir b&ouml;lge idare mahkemesinin kararı, Danıştay\'a temyiz edilebilir. İtirazlar, &uuml;st mahkemeler tarafından incelenir ve son karar verilir.</p>\r\n<p><strong>Sonu&ccedil;:</strong></p>\r\n<p>İdare hukukunda tam yargı davaları, karmaşık ve detaylı bir s&uuml;re&ccedil;tir. Davacılar, davalarını a&ccedil;madan &ouml;nce hukuki yardım alarak, dava a&ccedil;ma şartlarını ve s&uuml;relerini dikkatlice takip etmelidirler. Bu yazı, tam yargı davalarının aşamalarına genel bir bakış sunmaktadır. Ancak, her davada &ouml;zel durumlar ve farklılıklar olabileceği unutulmamalıdır. Bu nedenle, hukuki destek almak her zaman &ouml;nemlidir.</p>', NULL, 'e6fbaffc-f4da-45b9-876a-346d4324cd02.jpg', 3, NULL, 0, 1, '2025-09-04 19:18:40', '2025-09-10 13:45:16', NULL, 30, NULL, NULL);
INSERT INTO `blogs` VALUES (28, 8, 'Anayasa Mahkemesi\'nde Bireysel Başvuru: Adaletin Son Sığınağı', 'anayasa-mahkemesinde-bireysel-basvuru-adaletin-son-siginagi', '<p>Merhaba değerli okuyucular! Bug&uuml;n, temel hak ve &ouml;zg&uuml;rl&uuml;klerin ihlali durumunda bireylerin başvurabileceği &ouml;nemli bir mekanizma olan Anayasa Mahkemesi\'ndeki bireysel başvuru s&uuml;recini ele alacağız. Bu s&uuml;re&ccedil;, demokratik hukuk devletlerinde vatandaşların haklarının korunması i&ccedil;in son derece &ouml;nemli bir g&uuml;vencedir. Ancak, s&uuml;recin karmaşıklığı nedeniyle, bir&ccedil;ok kişi başvuru şartlarını ve s&uuml;reci tam olarak bilmemektedir. Bu yazıda, bireysel başvuru s&uuml;recini adım adım a&ccedil;ıklayarak, bu konuda size daha fazla bilgi vereceğiz.</p>\r\n<p><strong>Başvuru Şartları:</strong></p>\r\n<p>Anayasa Mahkemesi\'ne bireysel başvuru yapabilmek i&ccedil;in bazı şartların yerine getirilmesi gerekmektedir. Bunlar kısaca ş&ouml;yle sıralanabilir:</p>\r\n<ul>\r\n<li><strong>İhlal Edilen Hak:</strong> Başvurunun, Anayasa\'da g&uuml;vence altına alınmış temel hak ve &ouml;zg&uuml;rl&uuml;klerden birinin ihlal edilmesine dayanması gerekir.</li>\r\n<li><strong>Yargı Yolu T&uuml;ketilmesi:</strong> İdari veya adli yargı yollarının t&uuml;ketilmiş olması şarttır. Yani, ilgili t&uuml;m yargı mercilerine başvurulmuş ve sonu&ccedil; alınmamış olmalıdır. İstisnai durumlar elbette mevcuttur, &ouml;rneğin, yargı yolunun aşırı uzun s&uuml;rmesi veya etkili bir yargı yolunun olmaması gibi.</li>\r\n<li><strong>Başvuru S&uuml;resi:</strong> Başvurunun, ihlalin &ouml;ğrenildiği tarihten itibaren altı ay i&ccedil;inde yapılması gerekir. Bu s&uuml;re, bazı istisnai durumlar dışında uzatılamaz.</li>\r\n<li><strong>Başvuru Usul&uuml;:</strong> Başvurunun, Anayasa Mahkemesi\'nin belirlediği usul ve bi&ccedil;im kurallarına uygun olarak yapılması zorunludur. Bu, başvuru dilek&ccedil;esinin i&ccedil;eriği, eklenmesi gereken belgeler ve sunulması gereken deliller a&ccedil;ısından &ouml;nem taşır.</li>\r\n</ul>\r\n<p><strong>Kabul Edilebilirlik İncelemesi:</strong></p>\r\n<p>Anayasa Mahkemesi, başvuruyu &ouml;ncelikle kabul edilebilirlik a&ccedil;ısından inceler. Bu incelemede, başvurunun yukarıda belirtilen şartları karşılayıp karşılamadığı, yetkisinin olup olmadığı ve benzeri hususlar değerlendirilir. Kabul edilebilirlik incelemesinin olumsuz sonu&ccedil;lanması durumunda, başvuru reddedilir.</p>\r\n<p><strong>Esasa İlişkin İnceleme:</strong></p>\r\n<p>Başvurunun kabul edilebilir bulunması durumunda, Anayasa Mahkemesi esasa ilişkin incelemeye başlar. Bu aşamada, başvuruda iddia edilen temel hak ve &ouml;zg&uuml;rl&uuml;k ihlalinin olup olmadığı, ihlalin niteliği ve sonu&ccedil;ları incelenir. Mahkeme, gerekirse tarafları dinler, delilleri değerlendirir ve gerekli g&ouml;rd&uuml;ğ&uuml; diğer işlemleri yapar.</p>\r\n<p><strong>Karar ve Kararın Bağlayıcılığı:</strong></p>\r\n<p>Esasa ilişkin incelemenin sonunda, Anayasa Mahkemesi bir karar verir. Bu karar, kesin ve bağlayıcıdır. Karar, gerek&ccedil;eli olarak yazılır ve gerek&ccedil;esinde, Mahkeme\'nin kararına ulaşırken dikkate aldığı hususlar detaylı bir şekilde a&ccedil;ıklanır. Karar, ilgili kamu kurum ve kuruluşları ile bireyler i&ccedil;in bağlayıcıdır ve kararın uygulanması gerekmektedir. Kararın uygulanmaması durumunda, Anayasa Mahkemesi, kararının uygulanmasını sağlamak i&ccedil;in &ccedil;eşitli yaptırımlar uygulayabilir.</p>\r\n<p><strong>Sonu&ccedil;:</strong></p>\r\n<p>Anayasa Mahkemesi\'ne bireysel başvuru, temel hak ve &ouml;zg&uuml;rl&uuml;klerin ihlali durumunda bireylerin son &ccedil;are olarak başvurabilecekleri &ouml;nemli bir mekanizmadır. Ancak, s&uuml;recin karmaşıklığı ve uzun s&uuml;rmesi g&ouml;z &ouml;n&uuml;nde bulundurulmalıdır. Bu nedenle, başvuru yapmadan &ouml;nce mutlaka uzman bir avukattan yardım alınması &ouml;nerilir. Bu yazının, s&uuml;re&ccedil; hakkında genel bir bilgi verdiğini ve hukuki tavsiye niteliği taşımadığını hatırlatmak isterim. Her durumun kendine &ouml;zg&uuml; şartları bulunduğundan, mutlaka detaylı bir hukuki danışmanlık alınması gereklidir.</p>', NULL, 'cecbf02f-066a-41c6-bcae-56618ca39d5c.jpg', 3, NULL, 0, 1, '2025-09-04 19:19:44', '2025-09-10 13:45:16', NULL, 31, NULL, NULL);
INSERT INTO `blogs` VALUES (31, 7, 'Türk Borçlar Kanunu\'nda Kira Sözleşmelerinden Doğan Uyuşmazlıkların Çözümü: Tahliye, Kira Tespit ve Arabuluculukssssssssss', 'turk-borclar-kanununda-kira-sozlesmelerinden-dogan-uyusmazliklarin-cozumu-tahliye-kira-tespit-ve-arabuluculukssssssssss', '<p>Kira s&ouml;zleşmeleri, g&uuml;nl&uuml;k hayatta sık&ccedil;a karşılaşılan ve kimi zaman uyuşmazlıklara yol a&ccedil;abilen &ouml;nemli hukuki işlemlerdir. T&uuml;rk Bor&ccedil;lar Kanunu (TBK), bu uyuşmazlıkların &ccedil;&ouml;z&uuml;m&uuml; i&ccedil;in &ccedil;eşitli yollar sunmaktadır. En sık karşılaşılan sorunlar ve &ccedil;&ouml;z&uuml;m yollarına yakından bakalım.</p>\r\n<p><strong>1. Tahliye Davaları:</strong></p>\r\n<p>Kira s&ouml;zleşmesinin sona ermesinden sonra kiracının taşınmazı boşaltmaması durumunda, malik tarafından tahliye davası a&ccedil;ılabilir. TBK, tahliye nedenlerini detaylı bir şekilde d&uuml;zenlemiştir. Bu nedenler arasında kira s&ouml;zleşmesinin s&uuml;resinin dolması, kiracının kira bedelini &ouml;dememesi, s&ouml;zleşmede belirtilen y&uuml;k&uuml;ml&uuml;l&uuml;klere aykırı davranması, taşınmazı amacı dışında kullanması gibi durumlar yer alır. Tahliye davası, sulh hukuk mahkemelerinde g&ouml;r&uuml;l&uuml;r. Davacı, tahliye talebini gerek&ccedil;elendiren delilleri mahkemeye sunmalıdır. Mahkeme, delilleri değerlendirerek tahliye kararı verebilir veya davanın reddine karar verebilir. Tahliye kararının infazı i&ccedil;in icra takibi başlatılır.</p>\r\n<p><strong>2. Kira Tespit Davaları:</strong></p>\r\n<p>Kira bedeli konusunda taraflar arasında anlaşmazlık &ccedil;ıkması durumunda, kira tespit davası a&ccedil;ılabilir. Bu davada, mahkeme s&ouml;zleşmede belirtilen kira bedelinin ge&ccedil;erli olup olmadığını veya s&ouml;zleşmede kira bedeli belirtilmemişse, b&ouml;lgedeki piyasa koşullarına g&ouml;re makul bir kira bedeli tespit eder. Kira tespit davası da sulh hukuk mahkemelerinde g&ouml;r&uuml;l&uuml;r. Mahkeme, bilirkişi raporu veya diğer deliller ışığında adil bir kira bedeli belirler.</p>\r\n<p><strong>3. Arabuluculuk S&uuml;reci:</strong></p>\r\n<p>TBK, uyuşmazlıkların mahkemeye gitmeden &ccedil;&ouml;z&uuml;lmesi i&ccedil;in arabuluculuğu teşvik eder. Kiracı ve malik, anlaşmazlıklarını arabulucu yardımıyla &ccedil;&ouml;zmeyi deneyebilirler. Arabuluculuk s&uuml;recinde, tarafsız bir arabulucu, tarafların g&ouml;r&uuml;şmelerine yardımcı olur ve karşılıklı anlaşmaya varılmasını sağlar. Arabuluculuk s&uuml;recinin avantajları şunlardır:</p>\r\n<ul>\r\n<li><strong>Hızlı ve ekonomik &ccedil;&ouml;z&uuml;m:</strong> Mahkeme s&uuml;re&ccedil;lerine g&ouml;re daha hızlı ve daha d&uuml;ş&uuml;k maliyetlidir.</li>\r\n<li><strong>Esneklik:</strong> Taraflar, kendi aralarında anlaşmaya varabilecekleri bir &ccedil;&ouml;z&uuml;m &uuml;retebilirler.</li>\r\n<li><strong>Gizlilik:</strong> Arabuluculuk s&uuml;recinde yapılan g&ouml;r&uuml;şmeler gizli tutulur.</li>\r\n<li><strong>Karşılıklı anlayış:</strong> Arabuluculuk, taraflar arasında karşılıklı anlayış ve uzlaşmayı sağlar.</li>\r\n</ul>\r\n<p>Arabuluculuk s&uuml;reci başarısız olursa, taraflar mahkemeye başvurabilirler. Ancak, arabuluculuk s&uuml;reci, bir&ccedil;ok kira uyuşmazlığının mahkemeye ulaşmadan &ccedil;&ouml;z&uuml;lmesine yardımcı olabilir.</p>\r\n<p><strong>Sonu&ccedil;:</strong></p>\r\n<p>T&uuml;rk Bor&ccedil;lar Kanunu, kira s&ouml;zleşmelerinden doğan uyuşmazlıkların &ccedil;&ouml;z&uuml;m&uuml; i&ccedil;in &ccedil;eşitli yollar sunmaktadır. Taraflar, &ouml;ncelikle arabuluculuk yoluyla anlaşmaya varmaya &ccedil;alışmalıdır. Anlaşma sağlanamaması durumunda ise, tahliye veya kira tespit davaları yoluyla mahkemeden &ccedil;&ouml;z&uuml;m talep edilebilir. Her iki durumda da, hukuki danışmanlık almak, haklarınızı korumak ve en uygun &ccedil;&ouml;z&uuml;m&uuml; bulmak i&ccedil;in olduk&ccedil;a &ouml;nemlidir.</p>', NULL, '56c66b11-0cc5-4cb6-87c4-e7fb488f6b61.jpg', 1, 2, 1, 1, '2025-09-05 11:08:31', '2025-09-10 13:45:29', NULL, 31, NULL, NULL);
INSERT INTO `blogs` VALUES (32, 6, 'Türk Medeni Kanunu\'nda Velayet Davaları', 'turk-medeni-kanununda-velayet-davalari', '<p>Velayet davaları, aile hukukunun en hassas ve karmaşık konularından birini oluşturur. &Ccedil;ocukların refahı ve geleceği a&ccedil;ısından b&uuml;y&uuml;k &ouml;nem taşıyan bu davalarda, doğru adımların atılması ve hukuki s&uuml;re&ccedil;lerin titizlikle takip edilmesi hayati &ouml;nem taşır. T&uuml;rk Medeni Kanunu\'na (TMK) g&ouml;re velayet davasının aşamalarını ve avukatın bu s&uuml;re&ccedil;teki rol&uuml;n&uuml; detaylı olarak inceleyelim.</p>\r\n<p><strong>Dava A&ccedil;ma Şartları:</strong></p>\r\n<p>TMK\'ya g&ouml;re, velayet davası a&ccedil;abilmek i&ccedil;in belirli şartların yerine getirilmesi gerekir. Bunlar genellikle;</p>\r\n<ul>\r\n<li><strong>Velayetin paylaştırılması veya değiştirilmesi talebi:</strong> Eşler arasında velayetin nasıl d&uuml;zenleneceği konusunda anlaşmazlık olması,</li>\r\n<li><strong>Velayet yetkisinin k&ouml;t&uuml;ye kullanılması iddiası:</strong> Velayet sahibi kişinin &ccedil;ocuğun menfaatlerine aykırı davranışlarda bulunması iddiası,</li>\r\n<li><strong>&Ccedil;ocuğun refahı i&ccedil;in gerekli olması:</strong> &Ccedil;ocuğun gelişimini olumsuz etkileyen bir durumun varlığı ve velayetin değiştirilmesinin &ccedil;ocuğun yararına olacağının ispatı, gibi durumlardır.</li>\r\n</ul>\r\n<p>Dava, velayete ilişkin anlaşmazlığın taraflarından biri tarafından a&ccedil;ılır.</p>\r\n<p><strong>Velayet Davasının Aşamaları:</strong></p>\r\n<p><strong>1. &Ouml;n İnceleme:</strong></p>\r\n<p>Dava dilek&ccedil;esi mahkemeye sunulduktan sonra, mahkeme &ouml;ncelikle davanın usul&uuml;ne uygun olup olmadığını ve a&ccedil;ma şartlarının ger&ccedil;ekleşip ger&ccedil;ekleşmediğini inceler. Eksik evrak veya bilgi varsa taraflar tamamlamaya davet edilir. Bu aşamada, avukatın doğru ve eksiksiz bir dilek&ccedil;e hazırlaması ve gerekli delilleri sunması olduk&ccedil;a &ouml;nemlidir.</p>\r\n<p><strong>2. Tahkikat:</strong></p>\r\n<p>&Ouml;n incelemenin ardından, mahkeme tahkikat aşamasına ge&ccedil;er. Bu aşamada;</p>\r\n<ul>\r\n<li><strong>Tarafların dinlenmesi:</strong> Her iki taraf da mahkemede g&ouml;r&uuml;şlerini ve iddialarını detaylı olarak a&ccedil;ıklar.</li>\r\n<li><strong>Tanık dinlenmesi:</strong> Gerekli g&ouml;r&uuml;ld&uuml;ğ&uuml; takdirde, olayı bilen kişiler tanık olarak dinlenir.</li>\r\n<li><strong>Delil toplanması:</strong> Mahkeme, gerekli g&ouml;rd&uuml;ğ&uuml; diğer delilleri (fotoğraflar, mesajlar, raporlar vb.) toplar.</li>\r\n<li><strong>&Ccedil;ocuğun g&ouml;r&uuml;ş&uuml;n&uuml;n alınması:</strong> Belirli bir yaşın &uuml;zerindeki &ccedil;ocuğun g&ouml;r&uuml;ş&uuml;, &ccedil;ocuğun refahı a&ccedil;ısından b&uuml;y&uuml;k &ouml;nem taşır ve mahkeme tarafından dikkate alınır. &Ccedil;ocuğun g&ouml;r&uuml;ş&uuml;n&uuml;n nasıl alınacağına mahkeme karar verir.</li>\r\n</ul>\r\n<p>Avukatın bu aşamadaki rol&uuml; olduk&ccedil;a kritiktir. İstemciyi doğru şekilde y&ouml;nlendirmek, delilleri etkili bir şekilde sunmak, tanıkları hazırlamak ve karşı tarafın iddialarına karşı etkili bir savunma oluşturmak avukatın sorumluluğundadır.</p>\r\n<p><strong>3. Uzman Raporu:</strong></p>\r\n<p>Mahkeme, &ccedil;ocuğun menfaatlerinin belirlenmesi i&ccedil;in &ccedil;ocuğun psikolojik ve sosyal durumunu değerlendiren uzman (psikolog, psikiyatrist, sosyal hizmet uzmanı) raporuna başvurabilir. Bu rapor, mahkemenin karar vermesinde &ouml;nemli bir etkendir. Avukat, bu raporun hazırlanması aşamasında uzmanla iletişim kurarak, istemcisinin menfaatlerini en iyi şekilde koruyabilir.</p>\r\n<p><strong>4. Karar:</strong></p>\r\n<p>T&uuml;m deliller ve uzman raporu değerlendirildikten sonra, mahkeme velayet konusunda karar verir. Karar, &ccedil;ocuğun &uuml;st&uuml;n yararı g&ouml;zetilerek verilir. Mahkeme, velayeti tek taraflı olarak bir ebeveynin veya her iki ebeveynin m&uuml;şterek olarak &uuml;stlenmesini kararlaştırabilir. Ayrıca, nafaka, g&ouml;r&uuml;şme hakları gibi diğer konulara da karar verilir.</p>\r\n<p><strong>Avukatın Rol&uuml;:</strong></p>\r\n<p>Avukat, velayet davasında m&uuml;vekkilinin haklarını korumak ve en iyi sonucu elde etmek i&ccedil;in &ouml;nemli bir rol oynar. Avukatın g&ouml;revleri arasında;</p>\r\n<ul>\r\n<li><strong>Dava dilek&ccedil;esinin hazırlanması:</strong> Doğru ve eksiksiz bir dilek&ccedil;e hazırlamak,</li>\r\n<li><strong>Delil toplanması ve sunulması:</strong> Gerekli delilleri toplamak ve mahkemeye etkili bir şekilde sunmak,</li>\r\n<li><strong>Tarafların dinlenmesinde m&uuml;vekkili temsil etmek:</strong> M&uuml;vekkilini mahkemede savunmak,</li>\r\n<li><strong>Uzman raporuyla ilgili s&uuml;re&ccedil;leri takip etmek:</strong> Uzman raporunun hazırlanması ve değerlendirilmesi aşamalarında aktif rol almak,</li>\r\n<li><strong>Mahkeme kararına itiraz etmek:</strong> Karardan memnun kalınılmadığı durumlarda istinaf veya temyiz yoluyla itirazda bulunmak yer alır.</li>\r\n</ul>\r\n<p>Velayet davaları son derece hassastır ve profesyonel bir avukatın yardımı, m&uuml;vekkilin haklarının korunması ve &ccedil;ocuğun menfaatlerinin g&ouml;zetilmesi i&ccedil;in olmazsa olmazdır. Bu nedenle, velayet davası a&ccedil;mayı d&uuml;ş&uuml;nenlerin mutlaka deneyimli bir aile hukuku avukatından destek almaları &ouml;nerilir.</p>', NULL, 'd1f70bf0-67cf-4349-924a-52beaefe52af.jpg', 1, 3, 0, 1, '2025-09-05 11:09:52', '2025-09-10 13:45:29', NULL, 28, NULL, NULL);
INSERT INTO `blogs` VALUES (47, 3, 'Adaletin Kalkanı: Ceza Yargılamasında Savunma Hakkının Vazgeçilmez Kapsamı', 'adaletin-kalkani-ceza-yargilamasinda-savunma-hakkinin-vazgecilmez-kapsami', '<h2 id=\"adaletinkalkancezayarglamasndasavunmahakknnvazgeilmezkapsam\">Adaletin Kalkanı: Ceza Yargılamasında Savunma Hakkının Vazge&ccedil;ilmez Kapsamı</h2>\r\n<p>Ceza yargılamasında savunma hakkı, adil yargılanma hakkının temel ve vazge&ccedil;ilmez bir unsurudur. Hukuk devletinin en &ouml;nemli g&ouml;stergelerinden biri olan bu hak, bireyin devletin cezalandırma g&uuml;c&uuml;ne karşı kendisini koruyabilmesini, iddialara karşı arg&uuml;manlar sunabilmesini ve lehindeki delilleri ortaya koyabilmesini sağlar. T&uuml;rkiye Cumhuriyeti Anayasası\'nın 36. maddesi ve Avrupa İnsan Hakları S&ouml;zleşmesi\'nin 6. maddesiyle g&uuml;vence altına alınan savunma hakkı, <strong>tıpkı adaletin kalkanı gibi</strong>, yargılamanın her aşamasında ş&uuml;pheli veya sanığın etkin bir bi&ccedil;imde kendisini savunmasını m&uuml;mk&uuml;n kılar.</p>\r\n<p>Bu yazıda, ceza yargılamasında savunma hakkının kapsamı, ş&uuml;pheli ve sanığın hakları, m&uuml;dafinin (savunma avukatının) rol&uuml;, sorgu ve duruşma aşamalarında bu hakkın nasıl kullanıldığı detaylı bir şekilde incelenecektir.</p>\r\n<hr />\r\n<h3 id=\"isavunmahakknntemelesaslarvekapsam\">I. Savunma Hakkının Temel Esasları ve Kapsamı</h3>\r\n<p>Savunma hakkı, sadece şekli bir beyanda bulunma imkanı değil, yargılama s&uuml;recinin tamamında etkin ve adil bir temsili ifade eder. Bu hak, aşağıdaki temel prensipleri i&ccedil;erir:</p>\r\n<ol>\r\n<li><strong>Silahların Eşitliği (Equality of Arms):</strong> İddia makamı (savcılık) ile savunma makamının yargılama i&ccedil;inde eşit imkanlara sahip olması anlamına gelir. Savunma, iddia makamının sahip olduğu t&uuml;m bilgilere ve delillere erişebilmeli, kendi delillerini sunabilmeli ve iddiaları &ccedil;&uuml;r&uuml;tebilecek arg&uuml;manlar geliştirebilmelidir.</li>\r\n<li><strong>Hukuki Dinlenilme Hakkı:</strong> Ş&uuml;pheli veya sanığın, iddia ve isnat edilen su&ccedil;a ilişkin her t&uuml;rl&uuml; a&ccedil;ıklamayı yapma, tanık ve delil g&ouml;sterme, hukuki nitelendirmelere karşı g&ouml;r&uuml;ş bildirme ve yargılamanın sonucunu etkileyecek her t&uuml;rl&uuml; talep ve itirazda bulunma hakkıdır.</li>\r\n<li><strong>Susma Hakkı:</strong> Kendisi aleyhine delil sunmaya zorlanamama, ifade vermeyi veya sorguda cevap vermeyi reddetme hakkıdır. Bu, kendi aleyhine tanıklık etmeme ilkesinin bir tezah&uuml;r&uuml;d&uuml;r.</li>\r\n<li><strong>M&uuml;dafiden Yararlanma Hakkı:</strong> Savunmanın hukuki bilgi ve deneyim gerektiren bir s&uuml;re&ccedil; olması nedeniyle, ş&uuml;pheli veya sanığın bir m&uuml;dafinin yardımından yararlanma hakkıdır.</li>\r\n<li><strong>Delillere Erişim ve Bunları &Ccedil;&uuml;r&uuml;tme Hakkı:</strong> Yargılamaya konu olan t&uuml;m delillere (soruşturma dosyasındaki her t&uuml;rl&uuml; belge, ifade, rapor vb.) erişim ve bu delillerin doğruluğunu, ge&ccedil;erliliğini ve hukuka uygunluğunu sorgulama, &ccedil;&uuml;r&uuml;tme hakkı.</li>\r\n<li><strong>Kendi Lehine Delil Sunma ve Tanık Dinletme Hakkı:</strong> Kendi savunmasını g&uuml;&ccedil;lendirmek amacıyla yeni deliller sunma, lehindeki tanıkları mahkemeye &ccedil;ağırma ve dinletme hakkı.</li>\r\n<li><strong>Yargılamaya Fiilen Katılma Hakkı:</strong> Ş&uuml;pheli veya sanığın, duruşmalarda hazır bulunma, tanık ve bilirkişilerin dinlenmesine katılma ve onlara soru sorma hakkı.</li>\r\n<li><strong>Gerek&ccedil;eli Karar ve Kanun Yollarına Başvurma Hakkı:</strong> Hakkında verilen kararın gerek&ccedil;elerini &ouml;ğrenme ve hukuka aykırı bulduğu kararlara karşı &uuml;st mahkemelere başvurma (istinaf, temyiz) hakkı.</li>\r\n</ol>\r\n<hr />\r\n<h3 id=\"iiphelivesannhaklar\">II. Ş&uuml;pheli ve Sanığın Hakları</h3>\r\n<p>Ceza Muhakemesi Kanunu (CMK), ş&uuml;pheli ve sanığın haklarını detaylı bir şekilde d&uuml;zenlemiştir. \"Ş&uuml;pheli\", soruşturma aşamasında su&ccedil; ş&uuml;phesi altında olan kişiyi; \"sanık\" ise kovuşturma aşamasında (iddianamenin kabul&uuml;yle başlayan) hakkında dava a&ccedil;ılmış kişiyi ifade eder (CMK m. 2/1-a,b). Bu ayrım, hakların başlangı&ccedil; anı a&ccedil;ısından &ouml;nem taşır.</p>\r\n<p><strong>A. Soruşturma Aşamasında Ş&uuml;phelinin Hakları:</strong></p>\r\n<ul>\r\n<li><strong>Susma Hakkı (CMK m. 147/1-e):</strong> Ş&uuml;pheli, ifadesi alınırken veya sorgusu yapılırken susma hakkına sahiptir. Bu hak kendisine hatırlatılmak zorundadır. Susma hakkını kullanması aleyhine yorumlanamaz.</li>\r\n<li><strong>M&uuml;dafi Tayin Etme Hakkı (CMK m. 147/1-c, 149):</strong> Ş&uuml;pheli, bir m&uuml;dafiin hukuki yardımından yararlanma hakkına sahiptir. Maddi durumu elverişli olmasa bile barodan kendisine bir m&uuml;dafi g&ouml;revlendirilmesini isteyebilir. Bu hak, ilk ifadenin alınmasından itibaren başlar.</li>\r\n<li><strong>M&uuml;dafi ile G&ouml;r&uuml;şme Hakkı (CMK m. 147/1-c, 149/3):</strong> Ş&uuml;pheli, m&uuml;dafii ile her zaman ve engelsiz bir şekilde g&ouml;r&uuml;şebilir. Bu g&ouml;r&uuml;şmeler kısıtlanamaz.</li>\r\n<li><strong>Yakınlarına Haber Verme Hakkı (CMK m. 147/1-d):</strong> G&ouml;zaltına alındığında veya tutuklandığında, yakınlarına haber verilmesini isteme hakkına sahiptir.</li>\r\n<li><strong>Su&ccedil;lamadan Haberdar Olma Hakkı (CMK m. 147/1-b):</strong> Kendisine isnat edilen su&ccedil;un ne olduğunu, hangi delillere dayanıldığını ve haklarını &ouml;ğrenme hakkına sahiptir.</li>\r\n<li><strong>Delillerin Toplanmasını İsteme Hakkı (CMK m. 160/2):</strong> Soruşturma makamından (savcılık), sadece aleyhindeki delillerin değil, lehine olan delillerin de toplanmasını talep etme hakkı.</li>\r\n<li><strong>İfade Alma ve Sorgu İşlemlerinde M&uuml;dafi Bulundurma Hakkı (CMK m. 149/1):</strong> Ş&uuml;phelinin ifadesi alınırken veya sorgusu yapılırken m&uuml;dafii hazır bulunabilir. M&uuml;dafi olmaksızın alınan ifadeler kural olarak delil olarak kullanılamaz.</li>\r\n<li><strong>Tutuklamaya İtiraz Hakkı (CMK m. 101/4):</strong> Tutuklama kararına karşı itiraz edebilir ve serbest bırakılmasını talep edebilir.</li>\r\n</ul>\r\n<p><strong>B. Kovuşturma (Duruşma) Aşamasında Sanığın Hakları:</strong></p>\r\n<p>Kovuşturma aşamasında ş&uuml;phelinin sahip olduğu haklar korunmakla birlikte, yargılamanın a&ccedil;ık ve aleni olması nedeniyle yeni haklar da devreye girer:</p>\r\n<ul>\r\n<li><strong>Duruşmada Hazır Bulunma Hakkı (CMK m. 188):</strong> Sanık, duruşmalarda hazır bulunma hakkına sahiptir. Ancak, istisnai durumlarda (&ouml;rneğin, g&uuml;venlik nedeniyle veya kendisinin istememesi halinde) duruşmadan &ccedil;ıkarılabilir.</li>\r\n<li><strong>Tanık ve Bilirkişilere Soru Sorma Hakkı (CMK m. 201/1):</strong> Sanık ve m&uuml;dafii, duruşmada dinlenen tanık ve bilirkişilere doğrudan soru sorma hakkına sahiptir.</li>\r\n<li><strong>Delil Sunma ve Dinletme Hakkı (CMK m. 215):</strong> Kendi lehine olan t&uuml;m delilleri (yazılı belge, tanık, bilirkişi raporu vb.) mahkemeye sunabilir ve bunların duruşmada değerlendirilmesini isteyebilir.</li>\r\n<li><strong>Duruşma Zaptını İnceleme Hakkı (CMK m. 222):</strong> Duruşma tutanaklarını inceleme ve varsa yanlışlıkların d&uuml;zeltilmesini talep etme hakkı.</li>\r\n<li><strong>Son S&ouml;z Hakkı (CMK m. 216/3):</strong> H&uuml;k&uuml;m verilmeden &ouml;nce, sanığa son s&ouml;z&uuml; sorulmak zorundadır. Bu, yargılamanın en &ouml;nemli g&uuml;vencelerinden biridir ve ihmali mutlak bozma sebebidir.</li>\r\n<li><strong>Terc&uuml;mandan Yararlanma Hakkı (CMK m. 202):</strong> T&uuml;rk&ccedil;e bilmeyen veya engelli olan sanık, yargılama boyunca &uuml;cretsiz bir terc&uuml;mandan yararlanma hakkına sahiptir.</li>\r\n<li><strong>Dosyayı İnceleme Hakkı (CMK m. 153/1):</strong> M&uuml;dafii aracılığıyla soruşturma ve kovuşturma dosyasındaki t&uuml;m belgelere ve delillere erişim hakkı. Bu hak, soruşturmanın gizliliğini tehlikeye atacak durumlarda kısıtlanabilir ancak bu kısıtlama ge&ccedil;ici ve gerek&ccedil;eli olmalıdır.</li>\r\n<li><strong>Hukuka Aykırı Delillere İtiraz Hakkı:</strong> Hukuka aykırı yollarla elde edilmiş delillerin (&ouml;rneğin, işkenceyle alınmış ifade, hukuka aykırı aramayla bulunmuş delil) mahkemece değerlendirilmemesini talep etme hakkı (CMK m. 217/2).</li>\r\n</ul>\r\n<hr />\r\n<h3 id=\"iiimdafininrolvenemi\">III. M&uuml;dafinin Rol&uuml; ve &Ouml;nemi</h3>\r\n<p>M&uuml;dafi (savunma avukatı), savunma hakkının etkin bir şekilde kullanılmasında kilit bir role sahiptir. M&uuml;dafiin başlıca g&ouml;rev ve sorumlulukları şunlardır:</p>\r\n<ol>\r\n<li><strong>Hukuki Destek ve Danışmanlık:</strong> Ş&uuml;pheli/sanığı hukuki s&uuml;re&ccedil;ler, hakları, olası sonu&ccedil;lar hakkında bilgilendirmek ve stratejik tavsiyelerde bulunmak.</li>\r\n<li><strong>Hukuki İşlemlere Katılım:</strong> G&ouml;zaltı, tutuklama, ifade alma, sorgu, keşif, teşhis gibi t&uuml;m muhakeme işlemlerinde hazır bulunmak.</li>\r\n<li><strong>Delillerin Toplanması ve Sunulması:</strong> Ş&uuml;pheli/sanık lehine olan delilleri araştırmak, toplamak ve mahkemeye sunmak. Tanıkları dinletmek, bilirkişi raporları hazırlatmak.</li>\r\n<li><strong>Hukuka Aykırılıkların Tespiti ve İtiraz:</strong> Yargılama s&uuml;recindeki usul ve esas hatalarını, hukuka aykırı delil elde etme y&ouml;ntemlerini tespit etmek ve bunlara karşı itirazda bulunmak.</li>\r\n<li><strong>Savunma Dilek&ccedil;eleri ve Beyanlar:</strong> Ş&uuml;pheli/sanık adına yazılı ve s&ouml;zl&uuml; savunmalar hazırlamak, dilek&ccedil;eler sunmak ve duruşmada beyanlarda bulunmak.</li>\r\n<li><strong>Kanun Yollarına Başvurma:</strong> Mahkeme kararlarına karşı istinaf, temyiz gibi kanun yollarına başvurmak ve bu s&uuml;re&ccedil;leri y&uuml;r&uuml;tmek.</li>\r\n<li><strong>Ş&uuml;pheli/Sanığın Haklarını Koruma:</strong> Yargılamanın her aşamasında m&uuml;vekkilinin haklarının ihlal edilmemesini sağlamak, adil yargılanma prensiplerinin uygulanmasını denetlemek.</li>\r\n</ol>\r\n<p><strong>Zorunlu M&uuml;dafiilik (CMK m. 150):</strong> T&uuml;rk hukukunda, bazı durumlarda m&uuml;dafiin bulunması zorunludur. Bunlar:</p>\r\n<ul>\r\n<li>Ş&uuml;pheli veya sanığın yaşının 18\'den k&uuml;&ccedil;&uuml;k olması,</li>\r\n<li>Sağır veya dilsiz olması,</li>\r\n<li>Akıl hastalığı nedeniyle kendisini savunamayacak durumda olması,</li>\r\n<li>Mahkumiyeti halinde alt sınırı beş yıldan fazla hapis cezasını gerektiren bir su&ccedil;la su&ccedil;lanması,</li>\r\n<li>G&ouml;zaltına alınması veya tutuklanması durumunda kendisinin istemesi halinde,</li>\r\n<li>Ka&ccedil;ak sanıklar hakkında yapılan duruşmalarda.<br />Bu durumlarda m&uuml;dafii yoksa mahkeme veya soruşturma makamı barodan re\'sen m&uuml;dafi atamasını ister. M&uuml;dafii olmaksızın yapılan işlemler, mutlak hukuka aykırılık teşkil eder ve yargılamanın yenilenmesi sebebidir.</li>\r\n</ul>\r\n<hr />\r\n<h3 id=\"ivsorguvedurumaaamalarndasavunmahakknnkullanlmas\">IV. Sorgu ve Duruşma Aşamalarında Savunma Hakkının Kullanılması</h3>\r\n<p>Savunma hakkı, yargılama s&uuml;recinin her aşamasında farklı tezah&uuml;rlerle kullanılır.</p>\r\n<p><strong>A. Soruşturma Aşamasında (İfade Alma ve Sorgu):</strong></p>\r\n<ul>\r\n<li><strong>Hakların Hatırlatılması (CMK m. 147):</strong> Ş&uuml;pheliye, ifadesine ge&ccedil;ilmeden &ouml;nce kimliği, isnat edilen su&ccedil;, susma hakkı, m&uuml;dafi isteme hakkı, delil toplama talebi hakları a&ccedil;ıklanır. Bu, etkin savunma hakkının başlangıcıdır.</li>\r\n<li><strong>M&uuml;dafi Refakati (CMK m. 149):</strong> Ş&uuml;phelinin m&uuml;dafii, ifade alma veya sorgu sırasında hazır bulunabilir ve ş&uuml;pheliye hukuki destek sağlayabilir. M&uuml;dafiin hazır bulunmaması halinde alınan ifadeler, CMK 147 ve 148. maddelerine aykırılık teşkil eder ve delil olarak kullanılamaz.</li>\r\n<li><strong>Susma Hakkının Kullanılması:</strong> Ş&uuml;pheli, sorulara cevap vermeyebilir. Bu durumu, ifade tutanağına ge&ccedil;irilmelidir ve aleyhine delil olarak değerlendirilemez.</li>\r\n<li><strong>Kanuna Aykırı Sorgu Y&ouml;ntemlerine İtiraz (CMK m. 148):</strong> Yasa dışı y&ouml;ntemlerle (işkence, k&ouml;t&uuml; muamele, tehdit, aldatma vb.) alınan ifadeler kesinlikle delil olarak kullanılamaz. M&uuml;dafi, bu t&uuml;r y&ouml;ntemlere karşı derhal itiraz etmeli ve hukuki yollara başvurmalıdır.</li>\r\n<li><strong>Lehe Delil Talebi:</strong> Ş&uuml;pheli veya m&uuml;dafii, soruşturma makamından lehine olan delillerin toplanmasını (tanık dinletme, belge inceletme vb.) talep edebilir.</li>\r\n</ul>\r\n<p><strong>B. Kovuşturma Aşamasında (Duruşma):</strong></p>\r\n<ul>\r\n<li><strong>Duruşma Başlangıcı ve Sanığın Sorgusu:</strong> Sanık, duruşmanın başında hazır bulunur ve iddianamedeki su&ccedil;lamalar kendisine okunur. Ardından mahkeme tarafından ilk sorgusu yapılır. Sanık, bu aşamada da susma hakkını kullanabilir veya savunmasını beyan edebilir.</li>\r\n<li><strong>Delillerin Tartışılması (CMK m. 206-217):</strong> Duruşmada sunulan t&uuml;m deliller (tanık ifadeleri, belgeler, bilirkişi raporları vb.) tarafların huzurunda tartışılır. Sanık ve m&uuml;dafii, her bir delil hakkında g&ouml;r&uuml;ş bildirebilir, itiraz edebilir, a&ccedil;ıklama isteyebilir veya bu delillerin hukuka uygunluğunu sorgulayabilir.</li>\r\n<li><strong>Tanık ve Bilirkişi Sorgulama (CMK m. 201):</strong> Hem iddia makamının hem de savunma makamının tanık ve bilirkişileri doğrudan sorgulama hakkı vardır. Bu, \"&ccedil;apraz sorgu\" olarak da bilinen ve iddiaların g&uuml;venilirliğini test etmeye yarayan &ouml;nemli bir ara&ccedil;tır.</li>\r\n<li><strong>Yeni Delil Sunma ve Talepte Bulunma (CMK m. 215):</strong> Sanık veya m&uuml;dafii, yargılamanın ilerleyen aşamalarında yeni ortaya &ccedil;ıkan veya daha &ouml;nce sunulmamış lehe delilleri mahkemeye sunabilir ve bunların değerlendirilmesini talep edebilir.</li>\r\n<li><strong>Hukuka Aykırı Delillerin Dışlanması (CMK m. 217/2):</strong> Hukuka aykırı yollarla elde edilmiş delillerin mahkeme tarafından h&uuml;kme esas alınmaması talebi, savunma hakkının en &ouml;nemli g&uuml;vencelerinden biridir. Yargıtay i&ccedil;tihatları ve CMK, bu t&uuml;r delillerin mutlak surette h&uuml;k&uuml;ms&uuml;z olduğunu kabul eder.</li>\r\n<li><strong>Esas Hakkında Savunma ve Son S&ouml;z (CMK m. 216/3):</strong> Delillerin tamamlanmasından sonra Cumhuriyet savcısı esas hakkındaki g&ouml;r&uuml;ş&uuml;n&uuml; a&ccedil;ıklar. Ardından sanık ve m&uuml;dafiine bu g&ouml;r&uuml;şe karşı savunmalarını yapma ve son s&ouml;zlerini s&ouml;yleme imkanı tanınır. Sanığın son s&ouml;z hakkı, usul hukukunun emredici bir kuralıdır ve ihmali mutlak bozma sebebidir.</li>\r\n</ul>\r\n<hr />\r\n<h3 id=\"vsavunmahakknnhlalininsonular\">V. Savunma Hakkının İhlalinin Sonu&ccedil;ları</h3>\r\n<p>Savunma hakkı, adil yargılanma ilkesinin temelini oluşturduğundan, bu hakkın ihlali ciddi hukuki sonu&ccedil;lar doğurur. CMK\'nın 289. maddesi, \"hukuka kesin aykırılık halleri\"ni d&uuml;zenler ve savunma hakkının ihlali, bu hallerden bir&ccedil;oğunu kapsar. &Ouml;rneğin:</p>\r\n<ul>\r\n<li>Sanığın duruşmada hazır bulunma hakkının kısıtlanması,</li>\r\n<li>Zorunlu m&uuml;dafiilik hallerinde m&uuml;dafi atanmaması,</li>\r\n<li>Sanığa son s&ouml;z hakkının verilmemesi,</li>\r\n<li>Hukuka aykırı yollarla elde edilmiş delillerin h&uuml;kme esas alınması,</li>\r\n<li>Ş&uuml;phelinin hakları hatırlatılmadan alınan ifadenin delil olarak kullanılması.</li>\r\n</ul>\r\n<p>Bu t&uuml;r ihlaller, verilen kararın istinaf veya temyiz incelemesinde bozulmasına yol a&ccedil;ar ve yargılamanın ilgili aşamasının yeniden yapılması gerekir.</p>\r\n<hr />\r\n<h3 id=\"sonu\">Sonu&ccedil;</h3>\r\n<p>Ceza yargılamasında savunma hakkı, bir bireyin devletin cezalandırma g&uuml;c&uuml; karşısında hukuksal g&uuml;vencelerini temsil eden hayati bir haktır. Ş&uuml;pheli ve sanığın t&uuml;m yargılama aşamalarında etkin bir şekilde kendisini savunabilmesi, m&uuml;dafiden yararlanabilmesi, delillere erişebilmesi ve hukuka aykırılıklara itiraz edebilmesi, adil bir yargılamanın olmazsa olmaz koşuludur. T&uuml;rk Ceza Muhakemesi Kanunu, Anayasa ve uluslararası s&ouml;zleşmelerle g&uuml;vence altına alınan bu hak, sadece bireyin menfaatini değil, aynı zamanda hukukun &uuml;st&uuml;nl&uuml;ğ&uuml;n&uuml; ve toplumsal adaleti de korur. Savunma hakkının tam ve eksiksiz bir şekilde kullanılmasına imkan tanımayan bir yargılama, meşruiyetini yitirmiş sayılır.</p>', NULL, '4929695e-b6c3-456f-9d27-ff2725a0d6a7.png', 7, 3, 1, 1, '2025-09-09 08:50:01', '2025-09-10 13:45:27', NULL, 28, NULL, NULL);
INSERT INTO `blogs` VALUES (48, 1, 'Kira Uyuşmazlıklarında Çözüm Yolları: Bilmeniz Gerekenler', 'kira-uyusmazliklarinda-cozum-yollari-bilmeniz-gerekenler', '<p>Ev sahibi ve kiracı ilişkileri, g&uuml;nl&uuml;k hayatımızın &ouml;nemli bir par&ccedil;ası. Ancak zaman zaman bu ilişkilerde beklenmedik p&uuml;r&uuml;zler ortaya &ccedil;ıkabilir ve bu p&uuml;r&uuml;zler uyuşmazlıklara d&ouml;n&uuml;şebilir. Neyse ki, T&uuml;rk Bor&ccedil;lar Kanunu, bu t&uuml;r durumlar i&ccedil;in belirli &ccedil;&ouml;z&uuml;m yolları sunuyor. Peki, kira s&ouml;zleşmelerinden doğan anlaşmazlıklar nasıl &ccedil;&ouml;z&uuml;l&uuml;r? Gelin, sık&ccedil;a karşılaşılan tahliye davaları, kira tespit davaları ve arabuluculuk s&uuml;recini birlikte inceleyelim.</p>\r\n<p><strong>&Ouml;nce Arabuluculuk: Hukuki S&uuml;recin Vazge&ccedil;ilmezi</strong></p>\r\n<p>Artık kira uyuşmazlıklarında mahkemeye başvurmadan &ouml;nce zorunlu bir adım var: Arabuluculuk. Bu sistem, tarafların bir arabulucu eşliğinde bir araya gelerek sorunlarını mahkemeye taşımadan, karşılıklı uzlaşıyla &ccedil;&ouml;zmelerini ama&ccedil;lar. Arabulucu, tarafsız bir &uuml;&ccedil;&uuml;nc&uuml; kişi olarak m&uuml;zakereleri y&ouml;netir, tarafların birbirlerini anlamalarına yardımcı olur ve &ccedil;&ouml;z&uuml;m &ouml;nerileri sunar. Amacı, herkesin kabul edebileceği, adil bir anlaşmaya varılmasını sağlamaktır. Arabuluculuk s&uuml;reci hem daha hızlı hem de &ccedil;oğu zaman mahkeme s&uuml;recine g&ouml;re daha uygun maliyetlidir. Eğer taraflar arabuluculukta anlaşamazlarsa, işte o zaman yargı yoluna başvurma hakkı doğar.</p>\r\n<p><strong>Tahliye Davaları: Ev Sahibinin Haklı Sebepleri</strong></p>\r\n<p>Kiracı ve ev sahibi arasında en sık karşılaşılan uyuşmazlıklardan biri, kiracının evden &ccedil;ıkarılması, yani tahliye durumudur. T&uuml;rk Bor&ccedil;lar Kanunu, ev sahibinin kiracıyı tahliye edebilmesi i&ccedil;in belirli haklı sebeplerin bulunmasını şart koşar. Bunlar arasında en bilinenleri şunlardır:</p>\r\n<ul>\r\n<li><strong>Kiranın &Ouml;denmemesi:</strong> Kiracının kira bedelini zamanında veya eksik &ouml;demesi durumunda ev sahibi belirli prosed&uuml;rleri uygulayarak tahliye davası a&ccedil;abilir.</li>\r\n<li><strong>Tahliye Taahh&uuml;d&uuml;:</strong> Kiracının belirli bir tarihte evi boşaltacağına dair yazılı bir taahh&uuml;t vermesi ve bu tarihte evi boşaltmaması durumunda, ev sahibi taahh&uuml;t tarihinden itibaren bir ay i&ccedil;inde dava a&ccedil;abilir.</li>\r\n<li><strong>İhtiya&ccedil; Nedeniyle Tahliye:</strong> Ev sahibinin veya bakmakla y&uuml;k&uuml;ml&uuml; olduğu kişilerin (eş, &ccedil;ocuk, torun gibi) konuta ihtiyacı olması durumunda da belirli şartlar altında tahliye davası a&ccedil;ılabilir. Ancak bu durumda, ihtiyacın ger&ccedil;ek ve samimi olması &ouml;nemlidir.</li>\r\n<li><strong>Yeniden İnşa veya İmar:</strong> Kiralananın yeniden inşası veya imarı amacıyla esaslı onarımı, genişletilmesi ya da değiştirilmesi gerekli ve bu işler sırasında kiralananın boşaltılması zorunlu ise, ev sahibi tahliye davası a&ccedil;abilir.</li>\r\n</ul>\r\n<p>Tahliye davaları hassas s&uuml;re&ccedil;lerdir ve her durum kendi i&ccedil;inde değerlendirilir.</p>\r\n<p><strong>Kira Tespit Davaları: Adaletli Kira Bedelini Belirlemek</strong></p>\r\n<p>&Ouml;zellikle y&uuml;ksek enflasyon d&ouml;nemlerinde kira bedellerinin g&uuml;ncel piyasa koşullarına uyarlanması ihtiyacı doğar. İşte tam da bu noktada kira tespit davaları devreye girer. Bu dava t&uuml;r&uuml;, kiranın başladığı tarihten itibaren beş yıl ge&ccedil;tikten sonra a&ccedil;ılabileceği gibi, beş yıl dolmadan da belirli şartlar altında (&ouml;rneğin kira s&ouml;zleşmesinde artış oranının belirtilmemiş olması veya yasal sınırların aşılması) g&uuml;ndeme gelebilir.</p>\r\n<p>Kira tespit davasında mahkeme, benzer konutların kira bedellerini, evin durumunu, &ccedil;evresel fakt&ouml;rleri ve enflasyon oranlarını dikkate alarak \"hak ve nesafet\" (adalet ve d&uuml;r&uuml;stl&uuml;k) ilkesi &ccedil;er&ccedil;evesinde yeni kira bedelini belirler. Bu dava hem ev sahibi hem de kiracı tarafından a&ccedil;ılabilir ve amacı, taraflar arasında adil bir kira bedeli dengesi kurmaktır.</p>\r\n<p><strong>Sonu&ccedil;: Bilin&ccedil;li Olmak ve Profesyonel Destek Almak &Ouml;nemli</strong></p>\r\n<p>Kira uyuşmazlıkları, bazen karmaşık hukuki s&uuml;re&ccedil;ler i&ccedil;erebilir. T&uuml;rk Bor&ccedil;lar Kanunu, hem ev sahiplerini hem de kiracıları koruyan dengeli h&uuml;k&uuml;mler i&ccedil;erse de, bu hakların doğru şekilde kullanılması ve s&uuml;recin hatasız y&ouml;netilmesi b&uuml;y&uuml;k &ouml;nem taşır. Bir uyuşmazlıkla karşılaştığınızda, haklarınızı ve sorumluluklarınızı doğru anlamak i&ccedil;in bir avukattan hukuki danışmanlık almak, hem zaman hem de olası mali kayıpların &ouml;n&uuml;ne ge&ccedil;mek adına en doğru adımdır. Unutmayın, doğru bilgi ve profesyonel destek, sorunların en hızlı ve etkili şekilde &ccedil;&ouml;z&uuml;lmesini sağlar.</p>', NULL, 'c61a122b-9780-45db-b028-8f306ea4b06d.jpg', 3, NULL, 1, 1, '2025-09-10 13:07:05', '2025-09-10 13:45:27', NULL, 28, 'sssssssssssssssssEv sahibi-kiracı uyuşmazlıklarında çözüm yollarını öğrenin. Arabuluculuk, tahliye ve kira tespit davaları ile haklarınızı nasıl koruyacağınızı keşfedin.', 'ssssssskira uyuşmazlıkları, arabuluculuk, tahliye davası, kira tespit davası, ev sahibi hakları, kiracı hakları, kira hukuku, Türk Borçlar Kanunu, kira sözleşmesi');
INSERT INTO `blogs` VALUES (49, 4, 'aaaaaaaaaaa', 'aaaaaaaaaaa', '<div>asdasdadadad</div>', NULL, '34120b7a-7a22-4540-b58f-6b3ece0dbfe1.jpg', 6, 3, 0, 1, '2025-09-10 13:26:17', '2025-09-10 13:45:27', NULL, 28, 'ffffffffffffff', 'gggggggggggg');
INSERT INTO `blogs` VALUES (50, 2, 'Ceza Hukukunda Tutuklama: Şartları, Süreleri, Alternatifleri ve İtiraz Yolları', 'ceza-hukukunda-tutuklama-sartlari-sureleri-alternatifleri-ve-itiraz-yollari', '<p>Ceza hukukunda tutuklama, bir kişinin su&ccedil; ş&uuml;phesi altında yargılanması s&uuml;recinde, belirli koşulların varlığı halinde &ouml;zg&uuml;rl&uuml;ğ&uuml;nden mahrum bırakılmasına neden olan ge&ccedil;ici bir koruma tedbiridir. Anayasa ile g&uuml;vence altına alınan kişi &ouml;zg&uuml;rl&uuml;ğ&uuml; ve g&uuml;venliği hakkının istisnası olması sebebiyle, bu tedbirin uygulanması son derece sınırlı ve yasal şartlara bağlıdır. Amacı, yargılamanın sağlıklı ilerlemesini sağlamak ve h&uuml;km&uuml;n infazını teminat altına almaktır.</p>\r\n<p><strong>Tutuklama Kararı Verilebilmesinin Şartları</strong></p>\r\n<p>T&uuml;rk Ceza Muhakemesi Kanunu (CMK) madde 100&rsquo;e g&ouml;re bir kişi hakkında tutuklama kararı verilebilmesi i&ccedil;in &uuml;&ccedil; temel şartın birlikte bulunması gerekir:</p>\r\n<ol>\r\n<li><strong>Kuvvetli Su&ccedil; Ş&uuml;phesi:</strong> Tutuklama kararı verilebilmesi i&ccedil;in, somut delillere dayanan kuvvetli bir su&ccedil; ş&uuml;phesinin varlığı zorunludur. Bu ş&uuml;phe, sadece soyut bir ihtimalden ibaret olmamalı, mevcut delillerle desteklenmelidir.</li>\r\n<li><strong>Tutuklama Nedenlerinin Varlığı:</strong> Kuvvetli su&ccedil; ş&uuml;phesinin yanı sıra, aşağıda belirtilen tutuklama nedenlerinden en az birinin bulunması gereklidir:\r\n<ul>\r\n<li><strong>Ka&ccedil;ma Ş&uuml;phesi:</strong> Ş&uuml;phelinin/sanığın ka&ccedil;ma veya saklanma ihtimalinin y&uuml;ksek olması. &Ouml;zellikle su&ccedil;un niteliği, verilen veya verilecek ceza, ş&uuml;phelinin/sanığın durumu bu ş&uuml;pheyi doğurabilir.</li>\r\n<li><strong>Delilleri Karartma Ş&uuml;phesi:</strong> Ş&uuml;phelinin/sanığın delilleri yok etme, gizleme, değiştirme veya tanık, mağdur ya da başkaları &uuml;zerinde baskı yapma girişiminde bulunma ihtimali.</li>\r\n<li><strong>Katalog Su&ccedil;lar:</strong> CMK madde 100/3&rsquo;te sayılan belirli su&ccedil;lar (&ouml;rneğin; kasten &ouml;ld&uuml;rme, cinsel saldırı, uyuşturucu ticareti, ter&ouml;r su&ccedil;ları) i&ccedil;in, kanunda \"tutuklama nedeni var sayılabilir\" denilmektedir. Ancak bu, otomatik tutuklama anlamına gelmez; h&acirc;kim her somut olayın &ouml;zelliklerini değerlendirerek karar verir. Yine de bu su&ccedil;larda tutuklama nedeninin varlığına ilişkin karine, h&acirc;kimin işini kolaylaştırmaktadır.</li>\r\n</ul>\r\n</li>\r\n<li><strong>Orantılılık İlkesi:</strong> Tutuklama tedbirinin, ulaşılmak istenen ama&ccedil;la orantılı olması gerekir. İşin &ouml;nemi, verilmesi beklenen ceza veya g&uuml;venlik tedbiriyle &ouml;l&ccedil;&uuml;l&uuml; olmayan bir tutuklama kararı verilemez. Eğer adli kontrol gibi daha hafif bir tedbirle aynı amaca ulaşılabilecekse, tutuklama kararı verilemez.</li>\r\n</ol>\r\n<p><strong>Tutuklama S&uuml;releri</strong></p>\r\n<p>Tutuklama bir ge&ccedil;ici tedbir olduğundan, kanunda s&uuml;re sınırları belirlenmiştir:</p>\r\n<ul>\r\n<li><strong>Soruşturma Aşamasında:</strong>\r\n<ul>\r\n<li>Asliye Ceza Mahkemesinin g&ouml;revine giren işlerde tutukluluk s&uuml;resi en fazla <strong>1 yıl</strong>, Ağır Ceza Mahkemesinin g&ouml;revine giren işlerde ise en fazla <strong>2 yıldır</strong>.</li>\r\n<li>Ancak bu s&uuml;reler, zorunlu hallerde, gerek&ccedil;eleri g&ouml;sterilerek <strong>6 ay</strong> daha uzatılabilir. &Ccedil;ocuklar i&ccedil;in bu s&uuml;reler daha kısadır.</li>\r\n</ul>\r\n</li>\r\n<li><strong>Kovuşturma Aşamasında (Yargılama Sırasında):</strong>\r\n<ul>\r\n<li>Asliye Ceza Mahkemesinin g&ouml;revine giren işlerde en fazla <strong>1 yıl</strong>, Ağır Ceza Mahkemesinin g&ouml;revine giren işlerde ise en fazla <strong>3 yıldır</strong>.</li>\r\n<li>Bu s&uuml;reler de, zorunlu hallerde gerek&ccedil;eli olmak kaydıyla uzatılabilir. Ağır Ceza Mahkemesinin g&ouml;revine giren işlerde uzatma s&uuml;resi toplamda <strong>4 yılı</strong> ge&ccedil;emez.</li>\r\n</ul>\r\n</li>\r\n<li><strong>Ter&ouml;r ve Toplu Su&ccedil;larda:</strong> Bu su&ccedil;larda tutukluluk s&uuml;releri daha uzun olabilir ve &ouml;zel d&uuml;zenlemelere tabidir.</li>\r\n<li><strong>Periyodik İnceleme:</strong> H&acirc;kim veya mahkeme, tutukluluğun devamının gerekip gerekmeyeceğini soruşturma evresinde her 30 g&uuml;nde bir, kovuşturma evresinde ise duruşma aralarında veya her 30 g&uuml;nde bir resen değerlendirir.</li>\r\n</ul>\r\n<p><strong>Alternatif Koruma Tedbirleri (Adli Kontrol)</strong></p>\r\n<p>Tutuklama, en ağır koruma tedbiri olup, CMK madde 109 uyarınca \"tutuklama nedenleri varsa bile, tutuklama yerine adl&icirc; kontrol altına alınarak da beklenen amacın elde edilebileceği anlaşıldığı takdirde, ş&uuml;pheli veya sanık hakkında adl&icirc; kontrol uygulanabilir.\" Adli kontrol, tutuklamaya alternatif olarak getirilen ve kişi &ouml;zg&uuml;rl&uuml;ğ&uuml;n&uuml; daha az kısıtlayan bir tedbirdir.</p>\r\n<p>Adli kontrol tedbirlerinden bazıları şunlardır:</p>\r\n<ul>\r\n<li>Yurt dışına &ccedil;ıkış yasağı.</li>\r\n<li>Belirli yerlere gitmeme.</li>\r\n<li>Belirli aralıklarla karakola imza atma.</li>\r\n<li>Elektronik kelep&ccedil;e takılması.</li>\r\n<li>Mağdurdan uzaklaştırma.</li>\r\n<li>Belirli bir g&uuml;vence miktarı yatırma.</li>\r\n<li>Silah bulundurmama.</li>\r\n</ul>\r\n<p>H&acirc;kim veya mahkeme, somut olayın &ouml;zelliklerine g&ouml;re bu tedbirlerden bir veya birka&ccedil;ına h&uuml;kmedebilir. Adli kontrol tedbirlerinin ihlali durumunda, ş&uuml;pheli/sanık hakkında tutuklama kararı verilebilir.</p>\r\n<p><strong>Tutuklama Kararına İtiraz Yolları</strong></p>\r\n<p>Tutuklama kararları kesin değildir ve aleyhine itiraz edilebilir.</p>\r\n<ul>\r\n<li><strong>Kimler İtiraz Edebilir?</strong>\r\n<ul>\r\n<li>Ş&uuml;pheli/Sanık.</li>\r\n<li>M&uuml;dafii (avukatı).</li>\r\n<li>Yasal temsilcisi (&ouml;rneğin velisi veya vasisi).</li>\r\n<li>Eşi.</li>\r\n</ul>\r\n</li>\r\n<li><strong>Nereye ve Ne Zaman İtiraz Edilir?</strong>\r\n<ul>\r\n<li>Tutuklama kararına veya tutukluluğun devamı kararına karşı, kararın tefhiminden veya tebliğinden itibaren <strong>7 g&uuml;n i&ccedil;inde</strong> kararı veren mahkemeye/h&acirc;kimliğe bir dilek&ccedil;e ile itiraz edilebilir.</li>\r\n</ul>\r\n</li>\r\n<li><strong>İtirazın İncelenmesi:</strong>\r\n<ul>\r\n<li>İtiraz dilek&ccedil;esi kararı veren h&acirc;kimliğe/mahkemeye sunulur. Kararı veren makam, itirazı yerinde g&ouml;r&uuml;rse kararını d&uuml;zeltebilir.</li>\r\n<li>Eğer itirazı yerinde g&ouml;rmezse, dosyayı itirazı incelemeye yetkili mercie g&ouml;nderir.</li>\r\n<li>Soruşturma evresinde sulh ceza h&acirc;kiminin kararına itirazı, o yerde birden &ccedil;ok sulh ceza h&acirc;kimliği varsa numara olarak kendisini izleyen h&acirc;kimlik, tek sulh ceza h&acirc;kimliği varsa o yerdeki asliye ceza mahkemesi inceler.</li>\r\n<li>Kovuşturma evresinde ise asliye ceza mahkemesi kararlarına itirazı ağır ceza mahkemesi, ağır ceza mahkemesi kararlarına itirazı ise bir &uuml;st derecedeki ağır ceza mahkemesi inceler.</li>\r\n<li>İtirazı inceleyen merci, itirazın kendisine ulaştığı tarihten itibaren <strong>3 g&uuml;n i&ccedil;inde</strong> kararını verir.</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>Tutuklama tedbiri, modern hukuk devletinde istisnai bir durum olup, kişi &ouml;zg&uuml;rl&uuml;ğ&uuml;n&uuml;n korunması adına t&uuml;m yasal g&uuml;vencelerle &ccedil;evrelenmiştir. Bu nedenle, herhangi bir tutuklama kararı &ouml;ncesinde ve sonrasında hukuki destek almak hayati &ouml;nem taşır.</p>', NULL, 'b1ceb4b4-d210-48e4-934b-f1f0793211c9.jpg', 3, 3, 0, 1, '2025-09-10 13:27:10', '2025-09-10 13:45:27', NULL, 28, 'Ceza hukukunda tutuklama nedir? Şartları, süreleri, adli kontrol gibi alternatifleri ve itiraz yolları hakkında kapsamlı rehber. Haklarınızı öğrenin.', 'Ceza Hukukunda Tutuklama, Tutuklama Şartları, Tutukluluk Süreleri, Adli Kontrol, Tutuklamaya İtiraz, CMK Tutuklama, Koruma Tedbirleri, Tutuklama Nedenleri, Ceza Yargılaması, Hukuki Destek');
INSERT INTO `blogs` VALUES (51, 0, 'Tüketici Mahkemeleri ve Hakem Heyeti: Ayıplı Mal Davalarının Detaylı Süreci', 'tuketici-mahkemeleri-ve-hakem-heyeti-ayipli-mal-davalarinin-detayli-sureci', '<p>Hayatımızda satın aldığımız &uuml;r&uuml;nler veya faydalandığımız hizmetler bazen beklentilerimizi karşılamayabilir, hatta ayıplı (kusurlu) &ccedil;ıkabilir. Bu durumla karşılaştığınızda haklarınızı bilmek ve doğru adımları atmak, mağduriyetinizi gidermenin en &ouml;nemli yoludur. Peki, ayıplı mal veya hizmet nedeniyle a&ccedil;ılan davalar hangi aşamalardan ge&ccedil;er ve bu s&uuml;re&ccedil;te nelere dikkat etmek gerekir? Gelin, adım adım inceleyelim.</p>\r\n<h3 id=\"ayplmalveyahizmetnedir\">Ayıplı Mal veya Hizmet Nedir?</h3>\r\n<p>T&uuml;ketici Kanunu\'na g&ouml;re ayıplı mal veya hizmet, t&uuml;keticiye teslim edildiği tarihte, taraflarca kararlaştırılmış olan &ouml;rnek ya da modele uygun olmaması ya da objektif olarak sahip olması gereken &ouml;zellikleri taşımaması nedeniyle s&ouml;zleşmeye aykırı olan mal veya hizmettir. Kısacası, aldığınız &uuml;r&uuml;n&uuml;n veya hizmetin vaat edileni yapmaması, bozuk olması veya beklenen kaliteyi sunmamasıdır.</p>\r\n<h3 id=\"lkadmtketicihakemheyetlerithh\">İlk Adım: T&uuml;ketici Hakem Heyetleri (THH)</h3>\r\n<p>Ayıplı bir &uuml;r&uuml;n veya hizmetle karşılaştığınızda, genellikle başvurulması gereken ilk durak T&uuml;ketici Hakem Heyetleri\'dir. Bu heyetler, t&uuml;keticilerin belli parasal sınırlar dahilindeki uyuşmazlıklarını hızlı, kolay ve &uuml;cretsiz bir şekilde &ccedil;&ouml;zmek amacıyla kurulmuştur.</p>\r\n<ul>\r\n<li><strong>Ne Zaman Başvurulur?</strong> Her yıl belirlenen parasal sınırlar dahilindeki uyuşmazlıklar i&ccedil;in doğrudan THH\'ye başvurulur. Bu sınırı aşan durumlarda veya THH kararına itiraz etmek isterseniz t&uuml;ketici mahkemelerine gitmeniz gerekir.</li>\r\n<li><strong>S&uuml;re&ccedil;:</strong> THH\'ye başvuru dilek&ccedil;e ile yapılır. Genellikle e-Devlet &uuml;zerinden de online başvuru imkanı bulunmaktadır. Belgelerinizi (fatura, garanti belgesi, fotoğraflar vb.) eksiksiz sunmanız &ouml;nemlidir. Heyet, incelemesini yaparak genellikle 6 ay i&ccedil;inde bir karar verir. Karar, taraflar i&ccedil;in bağlayıcıdır.</li>\r\n</ul>\r\n<h3 id=\"davayolutketicimahkemeleri\">Dava Yolu: T&uuml;ketici Mahkemeleri</h3>\r\n<p>T&uuml;ketici Hakem Heyeti\'nin parasal sınırını aşan uyuşmazlıklarda veya THH kararına itiraz etmek istediğinizde T&uuml;ketici Mahkemeleri devreye girer. Bu mahkemeler, t&uuml;keticinin şikayetlerini yargı yoluyla &ccedil;&ouml;z&uuml;me kavuşturur.</p>\r\n<h3 id=\"ayplmalhizmetdavalarnnaamalar\">Ayıplı Mal/Hizmet Davalarının Aşamaları</h3>\r\n<p>T&uuml;ketici mahkemesinde a&ccedil;ılan bir dava, genel olarak şu aşamalardan ge&ccedil;er:</p>\r\n<ol>\r\n<li><strong>Dava Dilek&ccedil;esi ve Davanın A&ccedil;ılması:</strong>\r\n<ul>\r\n<li>Davanın ilk ve en kritik adımı, T&uuml;ketici Mahkemesi\'ne sunulan \"Dava Dilek&ccedil;esi\"dir. Dilek&ccedil;ede, yaşadığınız sorunu (ayıplı mal/hizmet), talebinizi (&uuml;r&uuml;n değişimi, bedel iadesi, onarım vb.), olayları kronolojik sırayla ve delillerinizi (fatura, garanti belgesi, fotoğraf, video, yazışmalar vb.) a&ccedil;ık&ccedil;a belirtmelisiniz. Davalı (satıcı/sağlayıcı) bilgileri de eksiksiz olmalıdır. Bu aşamada bir avukattan yardım almak, dilek&ccedil;enizin hukuken sağlam olmasını sağlar.</li>\r\n</ul>\r\n</li>\r\n<li><strong>&Ouml;n İnceleme ve Tahkikat Aşaması:</strong>\r\n<ul>\r\n<li>Dilek&ccedil;eniz mahkemeye sunulduktan sonra, mahkeme &ouml;ncelikle dilek&ccedil;ede eksiklik olup olmadığını kontrol eder. Ardından davalı tarafa tebligat g&ouml;nderilerek savunma yapması istenir. Taraflar, delillerini sunar ve iddialarını destekleyen belgeleri mahkemeye iletir. Mahkeme, bu aşamada duruşmalar yaparak tarafları dinler ve olayları aydınlatmaya &ccedil;alışır.</li>\r\n</ul>\r\n</li>\r\n<li><strong>Bilirkişi İncelemesi:</strong>\r\n<ul>\r\n<li>Ayıplı mal veya hizmet davalarında, &uuml;r&uuml;n&uuml;n veya hizmetin ger&ccedil;ekten ayıplı olup olmadığı, ayıbın neden kaynaklandığı gibi teknik konular genellikle uzmanlık gerektirir. Bu durumda mahkeme, alanında uzman bir veya birden fazla \"Bilirkişi\" atar. Bilirkişi, &uuml;r&uuml;n&uuml;/hizmeti inceler, tarafları dinler ve hazırladığı raporu mahkemeye sunar. Bilirkişi raporu, davanın seyrini b&uuml;y&uuml;k &ouml;l&ccedil;&uuml;de etkileyen &ouml;nemli bir delildir. Taraflar bu rapora itiraz edebilirler.</li>\r\n</ul>\r\n</li>\r\n<li><strong>S&ouml;zl&uuml; Yargılama ve H&uuml;k&uuml;m:</strong>\r\n<ul>\r\n<li>T&uuml;m deliller toplandıktan, bilirkişi raporları alındıktan ve tarafların savunmaları tamamlandıktan sonra mahkeme, s&ouml;zl&uuml; yargılama aşamasına ge&ccedil;er. Bu aşamada son beyanlar alınır ve mahkeme, t&uuml;m dosya kapsamını değerlendirerek kararını a&ccedil;ıklar. Karar, davanın reddi, kabul&uuml; veya kısmen kabul&uuml; şeklinde olabilir.</li>\r\n</ul>\r\n</li>\r\n<li><strong>Kanun Yolları (İstinaf ve Temyiz):</strong>\r\n<ul>\r\n<li>Mahkemenin verdiği karardan memnun olmayan taraf veya taraflar, kararın tebliğinden itibaren yasal s&uuml;reler i&ccedil;erisinde \"İstinaf\" yoluna başvurabilirler. İstinaf mahkemesi (B&ouml;lge Adliye Mahkemesi), yerel mahkemenin kararını hukuka ve olaya uygunluk y&ouml;n&uuml;nden yeniden inceler. İstinaf mahkemesi kararından sonra, belirli parasal sınırları aşan davalarda ise \"Temyiz\" yoluyla Yargıtay\'a başvurma hakkı bulunur. Yargıtay, kararın hukuka uygunluğunu denetler. Bu s&uuml;re&ccedil;ler sonucunda karar kesinleşir.</li>\r\n</ul>\r\n</li>\r\n</ol>\r\n<h3 id=\"tketicihakemheyetiiletketicimahkemesiarasndakitemelfarklar\">T&uuml;ketici Hakem Heyeti ile T&uuml;ketici Mahkemesi Arasındaki Temel Farklar</h3>\r\n<table>\r\n<thead>\r\n<tr>\r\n<th style=\"text-align: left;\">&Ouml;zellik</th>\r\n<th style=\"text-align: left;\">T&uuml;ketici Hakem Heyeti (THH)</th>\r\n<th style=\"text-align: left;\">T&uuml;ketici Mahkemesi</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td style=\"text-align: left;\"><strong>Parasal Sınır</strong></td>\r\n<td style=\"text-align: left;\">Her yıl belirlenen sınırlar dahilinde</td>\r\n<td style=\"text-align: left;\">THH sınırını aşan veya THH kararına itiraz</td>\r\n</tr>\r\n<tr>\r\n<td style=\"text-align: left;\"><strong>Başvuru</strong></td>\r\n<td style=\"text-align: left;\">Kolay, genellikle e-Devlet veya şahsen</td>\r\n<td style=\"text-align: left;\">Dilek&ccedil;e ile mahkemeye, avukat desteği &ouml;nerilir</td>\r\n</tr>\r\n<tr>\r\n<td style=\"text-align: left;\"><strong>Masraflar</strong></td>\r\n<td style=\"text-align: left;\">Genellikle &uuml;cretsiz</td>\r\n<td style=\"text-align: left;\">Dava har&ccedil;ları, vekalet &uuml;creti, bilirkişi &uuml;creti vb. masraflar olabilir</td>\r\n</tr>\r\n<tr>\r\n<td style=\"text-align: left;\"><strong>S&uuml;re&ccedil; Hızı</strong></td>\r\n<td style=\"text-align: left;\">Nispeten daha hızlı (genelde 6 ay)</td>\r\n<td style=\"text-align: left;\">Daha uzun s&uuml;rebilir (bilirkişi, istinaf, temyiz s&uuml;re&ccedil;leri)</td>\r\n</tr>\r\n<tr>\r\n<td style=\"text-align: left;\"><strong>Karar</strong></td>\r\n<td style=\"text-align: left;\">Bağlayıcıdır, itiraz edilmezse kesinleşir</td>\r\n<td style=\"text-align: left;\">Bağlayıcıdır, ancak istinaf ve temyiz yolu a&ccedil;ık</td>\r\n</tr>\r\n<tr>\r\n<td style=\"text-align: left;\"><strong>Temsil</strong></td>\r\n<td style=\"text-align: left;\">Avukat zorunlu değil</td>\r\n<td style=\"text-align: left;\">Avukat desteği hukuki yarar sağlar</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3 id=\"tketicilerenemlipular\">T&uuml;keticilere &Ouml;nemli İpu&ccedil;ları</h3>\r\n<ul>\r\n<li><strong>Delilleri Saklayın:</strong> Fatura, garanti belgesi, s&ouml;zleşme, e-posta yazışmaları, fotoğraf, video gibi t&uuml;m belgeleri mutlaka saklayın. Bunlar davanızın en &ouml;nemli kanıtları olacaktır.</li>\r\n<li><strong>Zaman Aşımı S&uuml;releri:</strong> Ayıplı mal ve hizmet davalarında belirli zaman aşımı s&uuml;releri bulunur. Ayıbın &ouml;ğrenildiği tarihten veya malın teslim tarihinden itibaren bu s&uuml;releri ka&ccedil;ırmamanız kritik &ouml;neme sahiptir.</li>\r\n<li><strong>Profesyonel Yardım:</strong> &Ouml;zellikle t&uuml;ketici mahkemelerinde dava a&ccedil;mayı d&uuml;ş&uuml;n&uuml;yorsanız, bir t&uuml;ketici hukuku avukatından destek almak hak kayıplarını &ouml;nlemek ve s&uuml;reci doğru y&ouml;netmek adına b&uuml;y&uuml;k fayda sağlayacaktır.</li>\r\n</ul>\r\n<h3 id=\"sonu\">Sonu&ccedil;</h3>\r\n<p>Ayıplı mal veya hizmetle karşılaşmak can sıkıcı olsa da, t&uuml;ketici olarak yasal haklara sahipsiniz. T&uuml;ketici Hakem Heyetleri ve T&uuml;ketici Mahkemeleri, bu haklarınızı aramanız i&ccedil;in size sunulan yasal yollardır. S&uuml;reci doğru adımlarla takip ederek ve gerekli delilleri toplayarak mağduriyetinizi gidermeniz m&uuml;mk&uuml;nd&uuml;r. Haklarınızı bilin ve gerektiğinde kullanmaktan &ccedil;ekinmeyin!</p>', NULL, 'f25ef06a-4ba0-4223-825e-7099fd7a2b87.jpg', 8, NULL, 0, 1, '2025-09-10 13:45:11', '2025-09-10 13:45:11', NULL, 28, 'Kusurlu ürün veya hizmet aldınız mı? Tüketici Hakem Heyeti ve Mahkemelerinde ayıplı mal davası süreçlerini, başvuru adımlarını, haklarınızı ve ipuçlarını keşfedin.', 'ayıplı mal davası, tüketici hakem heyeti, tüketici mahkemesi, tüketici hakları, kusurlu ürün, ayıplı hizmet, dava süreci, ürün iadesi');

